CREATE TABLE IF NOT EXISTS "organization" (
	"id" uuid PRIMARY KEY DEFAULT gen_random_uuid() NOT NULL,
	"name" text DEFAULT '' NOT NULL,
	"updated_at" timestamp DEFAULT CURRENT_TIMESTAMP,
	"created_at" timestamp DEFAULT CURRENT_TIMESTAMP NOT NULL,
	"api_import_id" text,
	"country_accounts_id" uuid,
	CONSTRAINT "organization___api_import_id_country_accounts_id" UNIQUE NULLS NOT DISTINCT(
		"name", 
		"api_import_id",
		"country_accounts_id"
	)
);

-- Add column only if it does not exist
ALTER TABLE "human_dsg_config"
ADD COLUMN IF NOT EXISTS "country_accounts_id" uuid;

-- Add foreign key constraint only if it does not exist
DO $$
BEGIN
    IF NOT EXISTS (
        SELECT 1
        FROM pg_constraint
        WHERE conname = 'human_dsg_config_country_accounts_id_country_accounts_id_fk'
    ) THEN
        ALTER TABLE "human_dsg_config"
        ADD CONSTRAINT "human_dsg_config_country_accounts_id_country_accounts_id_fk"
        FOREIGN KEY ("country_accounts_id")
        REFERENCES "public"."country_accounts"("id")
        ON DELETE CASCADE
        ON UPDATE NO ACTION;
    END IF;
END$$;

CREATE TYPE public.entity_validation_type AS ENUM (
    'hazardous_event',
    'disaster_event',
    'disaster_records'
);

CREATE TABLE IF NOT EXISTS public.entity_validation_assignment
(
    id uuid DEFAULT gen_random_uuid() PRIMARY KEY,
    entity_id uuid,
    entity_type public.entity_validation_type NOT NULL,
    assigned_to_user_id uuid,
    assigned_by_user_id uuid,
    assigned_at timestamp without time zone DEFAULT NOW(),
    CONSTRAINT fk_entity_validation_assignment_user_assigned_to_user_id FOREIGN KEY (assigned_to_user_id)
        REFERENCES public."user" (id) MATCH SIMPLE
        ON UPDATE NO ACTION
        ON DELETE NO ACTION
        NOT VALID,
    CONSTRAINT fk_entity_validation_assignment_user_assigned_by_user_id FOREIGN KEY (assigned_by_user_id)
        REFERENCES public."user" (id) MATCH SIMPLE
        ON UPDATE NO ACTION
        ON DELETE NO ACTION
        NOT VALID
);

CREATE TABLE IF NOT EXISTS public.entity_validation_rejection
(
    id uuid DEFAULT gen_random_uuid() PRIMARY KEY,
    entity_id uuid,
    entity_type public.entity_validation_type NOT NULL,
    rejected_by_user_id uuid,
    rejection_message text,
    rejected_at timestamp without time zone DEFAULT NOW(),
    CONSTRAINT fk_entity_validation_rejection_user_rejected_by_user_id FOREIGN KEY (rejected_by_user_id)
        REFERENCES public."user" (id) MATCH SIMPLE
        ON UPDATE NO ACTION
        ON DELETE NO ACTION
        NOT VALID
);

