	-- 5. Update code, name_en , description_en and cluster type id of specific hazards 
	UPDATE public.hip_hazard SET code= 'BI0603', name_en= 'Harmful Algal Blooms', description_en= 'Harmful algal blooms result from noxious and/or toxic algae that cause direct and indirect negative impacts on aquatic ecosystems, coastal resources, and human health (Kudela et al., 2015).', cluster_id = '1743' WHERE id = '78385';
	UPDATE public.hip_hazard SET code= 'BI0401', name_en= 'Insect Pest Infestations', description_en= 'An insect pest infestation is a recently detected insect pest population, including an incursion, or a sudden significant increase of an established insect in an area leading to damage to plants in production fields, forests or natural habitats and causing substantial damage to productivity, biodiversity or natural resources (adapted from IPPC Secretariat, 2024).', cluster_id = '1046' WHERE id = '78386';
	UPDATE public.hip_hazard SET code= 'BI0402', name_en= 'Locust upsurge', description_en= 'Widespread and heavy infestations of crops and natural vegetation by locusts causing significant threats to food security, livelihoods and natural habitats in multiple regions (adapted from FAO, 2009a).', cluster_id = '1046' WHERE id = '78387';
	UPDATE public.hip_hazard SET code= 'BI0403', name_en= 'Invasive Species, Including Weeds', description_en= '‘Invasive species’, also known as ‘alien invasive species’, are species whose introduction, establishment and spread into new areas threaten ecosystems, habitats or other species and cause social, economic or environmental harm, or harm to human health (FAO, 2007:82).', cluster_id = '1046' WHERE id = '78389';
	UPDATE public.hip_hazard SET code= 'BI0605', name_en= 'Snakebite envenoming', description_en= 'A snakebite envenoming is a potentially life-threatening disease caused by toxins in the bite of a venomous snake (WHO, 2023).', cluster_id = '1743' WHERE id = '78390';
	UPDATE public.hip_hazard SET code= 'BI0604', name_en= 'Human-Wildlife Conflict', description_en= 'Human-wildlife conflict is defined as struggles that emerge when the presence or behaviour of wildlife poses an actual or perceived, direct and recurring threat to human interests or needs, leading to disagreements between groups of people and negative impacts on people and/or wildlife (IUCN SSC, 2022).', cluster_id = '1743' WHERE id = '78391';
	UPDATE public.hip_hazard SET code= 'BI0602', name_en= 'Biological Agents', description_en= 'Biological and toxin agents are either microorganisms like viruses, bacteria or fungi, or toxic substances produced by living organisms that are produced and released deliberately to cause disease and death in humans, animals or plants (WHO, no date).Biological agents include bacteria, viruses, fungi, other microorganisms and their associated toxins. They have the ability to adversely affect human health in a variety of ways, ranging from relatively mild, allergic reactions to serious medical conditions-even death. In some forms, biological agents can also be weaponized for use in bioterrorism or other crimes (adapted from US OSHA, no date)', cluster_id = '1743' WHERE id = '78392';
	UPDATE public.hip_hazard SET code= 'SO0303', name_en= 'Suicide Cluster', description_en= 'The term ‘suicide cluster’ describes a situation in which more suicides than expected occur in terms of time, place, or both (PHE, 2019). Two types of suicide clusters can be distinguished (Joiner, 1999): clusters where suicides occur during a restricted time period and are related to actual or fictional media-related phenomena, and space-time clusters (or point clusters), where an unusually high number of suicides occur in a small geographical area, or institution, and over a relatively brief period of time (adapted from Joiner, 1999 and PHE, 2019).', cluster_id = '1082' WHERE id = '78393';
	UPDATE public.hip_hazard SET code= 'BI0601', name_en= 'Antimicrobial Resistance', description_en= 'Antimicrobial-resistant (AMR) microorganisms are bacteria, fungi, viruses, or parasites that evolve to withstand antimicrobial treatments, including antibiotics, antifungals, antivirals, and antiparasitics. These resistant microorganisms, make infections harder to treat, increasing the risk of disease spread, severe illness, and mortality. AMR is a major global public health and food security challenge and is driven by the misuse and overuse of antimicrobials in human medicine, animal health, and plant production and protection, as well as environmental contamination (adapted from WHO, 2023 and FAO, 2025).', cluster_id = '1743' WHERE id = '78394';
	UPDATE public.hip_hazard SET code= 'BI0501', name_en= 'Bacterial Plant Disease', description_en= 'A bacterial plant disease is the occurrence of plant diseases caused by bacterial microorganisms over large areas with significant impacts on crop and forest productivity or natural habitat (adapted from FAO, 2018).', cluster_id = '1052' WHERE id = '78396';
	UPDATE public.hip_hazard SET code= 'BI0502', name_en= 'Fungal Plant Disease', description_en= 'Fungal plant disease is the occurrence of plant diseases caused by fungal agents over large areas with significant impacts on crop productivity or natural habitats (adapted from Arneson, 2001 and Moore et al., 2019).', cluster_id = '1052' WHERE id = '78397';
	UPDATE public.hip_hazard SET code= 'BI0503', name_en= 'Viral, Phytoplasma and Viroid Plant Disease Outbreaks', description_en= 'Viral, phytoplasma and viroid plant disease outbreaks refer to sudden occurrence of plant diseases caused by viruses, phytoplasma (syn. mycoplasma-like organisms) and viroids over large areas with significant impact on crop production or natural habitats (adapted from Nakashima & Murata, 1993; Hammond & Owens, 2006; FAO / IPPC, 2016; Rubio et al., 2020).', cluster_id = '1052' WHERE id = '78398';
	UPDATE public.hip_hazard SET code= 'BI0201', name_en= 'Anthrax', description_en= 'Anthrax is a disease caused by the spore-forming bacteria Bacillus anthracis. Anthrax is primarily a disease of herbivorous animals, although all mammals, including humans, can contract it. In humans, anthrax manifests itself in three distinct patterns (cutaneous, gastrointestinal, inhalational) (adapted from WHO, FAO, & OIE, 2008; CDC, 2020).', cluster_id = '1844' WHERE id = '78399';
	UPDATE public.hip_hazard SET code= 'BI0101', name_en= 'Airborne Diseases', description_en= 'Airborne transmission of infectious agents refers to the transmission of disease caused by the dissemination of very small droplets that remain infectious when suspended in air over long distances and time, and potentially cause significant morbidity and mortality (adapted from WHO, 2020).', cluster_id = '1053' WHERE id = '78400';
	UPDATE public.hip_hazard SET code= 'BI0102', name_en= 'Bloodborne Viruses', description_en= 'Bloodborne viruses are viruses transmitted by direct contact with infected blood or other body fluids (adapted from WHO, 2023).', cluster_id = '1053' WHERE id = '78401';
	UPDATE public.hip_hazard SET code= 'BI0110', name_en= 'Waterborne Diseases', description_en= 'Contaminated water and poor sanitation are linked to transmission of diseases such as cholera, diarrhoea, dysentery, hepatitis A, hepatitis E, typhoid and polio (adapted from WHO, 2023a and WHO, 2023b).', cluster_id = '1053' WHERE id = '78402';
	UPDATE public.hip_hazard SET code= 'BI0104', name_en= 'Foodborne Diseases', description_en= 'Foodborne diseases are caused by contamination of food and occur at any stage of the food production, delivery and consumption chain. They can result from several forms of environmental contamination including pollution in water, soil or air, as well as unsafe food storage and processing. Foodborne diseases encompass a wide range of illnesses from diarrhoea to cancers (WHO, no date a).', cluster_id = '1053' WHERE id = '78403';
	UPDATE public.hip_hazard SET code= 'BI0106', name_en= 'Sexually Transmitted Infections', description_en= 'Sexually transmitted infections are transmitted through sexual contact, including vaginal, anal and oral sex and some can also be transmitted from mother-to-child during pregnancy, childbirth and breastfeeding (WHO, 2024).', cluster_id = '1053' WHERE id = '78404';
	UPDATE public.hip_hazard SET code= 'BI0105', name_en= 'Neglected Tropical Diseases', description_en= 'Neglected tropical diseases (NTDs) are a diverse group of conditions caused by a variety of pathogens (including viruses, bacteria, parasites, fungi and toxins) and associated with devastating health, social and economic consequences. NTDs are mainly prevalent among impoverished communities in tropical areas, although some have a much larger geographical distribution. (WHO, no date a).', cluster_id = '1053' WHERE id = '78405';
	UPDATE public.hip_hazard SET code= 'BI0107', name_en= 'Vaccine-Preventable Diseases', description_en= 'Vaccine-preventable diseases are those infectious diseases that can be prevented by vaccination (WHO, 2019).', cluster_id = '1053' WHERE id = '78406';
	UPDATE public.hip_hazard SET code= 'BI0108', name_en= 'Vector-borne diseases (VBD)', description_en= 'Vector-borne diseases encompass a variety of illnesses that are caused by the spread of pathogens by living organisms known as vectors. These infectious diseases can be transmitted via vectors among humans (e.g. malaria, dengue), among animals (e.g. African swine fever, East Coast fever), or from animals to humans (e.g. Nipah virus disease). Many of these vectors are bloodsucking insects, and mosquitoes are the best-known disease vectors. Other vectors include ticks, flies, sandflies, fleas, triatomine bugs and some species of freshwater aquatic snails (adapted from WOAH, 2024a; WHO, 2024).', cluster_id = '1053' WHERE id = '78407';
	UPDATE public.hip_hazard SET code= 'BI0109', name_en= 'Viral Haemorrhagic Fevers', description_en= 'Viral haemorrhagic fevers include a spectrum of relatively mild to severe life-threatening diseases characterized by sudden onset of muscle and joint pain, fever, bleeding and shock from loss of blood. In severe cases, one of the most prominent symptoms is bleeding, or haemorrhaging, from orifices and internal organs (WHO, no date a).', cluster_id = '1053' WHERE id = '78408';
	UPDATE public.hip_hazard SET code= 'BI0301', name_en= 'Infectious Animal Diseases (Not Zoonoses)', description_en= 'Non-zoonotic infectious animal diseases are not shared between animals and humans (WHO, FAO, & OIE, 2019).', cluster_id = '1054' WHERE id = '78410';
	UPDATE public.hip_hazard SET code= 'BI0113', name_en= 'Zoonotic Diseases', description_en= 'Zoonotic diseases, or zoonoses, are diseases shared between animals – including livestock, wildlife, and pets – and people. They can pose serious risks to both animal and human health and may have far-reaching impacts on economies and livelihoods and represent a major public health problem. Zoonotic diseases are commonly spread at the human-animal-environment interface – where people and animals interact with each other in their shared environment (adapted from WHO, FAO, WOAH, 2019 & WHO, 2020).', cluster_id = '1053' WHERE id = '78411';
	UPDATE public.hip_hazard SET code= 'BI0103', name_en= 'Diarrhoeal Diseases', description_en= 'Diarrhoeal diseases are infectious diseases, contaminants and other causes of diarrhoea. Diarrhoea is defined as the passage of three or more loose or liquid stools per day, or more frequently than is normal for the individual. Diarrhoeal disease is the third leading cause of death in children 1–59 months of age. It is both preventable and treatable. (WHO, 2024a).', cluster_id = '1053' WHERE id = '78412';
	UPDATE public.hip_hazard SET code= 'BI0230', name_en= 'Prion Diseases', description_en= 'Prion diseases are a family of rare progressive neurodegenerative disorders that affect both humans and animals (Adapted from CDC, 2024, and WHO, no date).', cluster_id = '1844' WHERE id = '78413';
	UPDATE public.hip_hazard SET code= 'BI0212', name_en= 'Hepatitis B', description_en= 'Hepatitis B is a vaccine-preventable disease, that is endemic and epidemic worldwide and is caused by the Hepatitis B virus (HBV). HBV can cause both acute and chronic liver disease. Chronic infection puts people at high risk of death from cirrhosis and liver cancer (WHO, 2024).', cluster_id = '1844' WHERE id = '78414';
	UPDATE public.hip_hazard SET code= 'BI0213', name_en= 'Hepatitis C', description_en= 'Hepatitis C is a blood-borne liver disease caused by the hepatitis C virus: the virus can cause both acute and chronic hepatitis, ranging in severity from a mild illness lasting a few weeks to a serious, lifelong illness including liver cirrhosis and liver cancer. Hepatitis C is endemic and epidemic worldwide (WHO, 2024a).', cluster_id = '1844' WHERE id = '78415';
	UPDATE public.hip_hazard SET code= 'BI0214', name_en= 'HIV and AIDS', description_en= 'Human immunodeficiency virus (HIV) is a virus that attacks the body’s immune system. Acquired immunodeficiency syndrome (AIDS) occurs at the most advanced stage of infection. HIV targets the body’s white blood cells, weakening the immune system. This makes it easier to get sick with diseases like tuberculosis, infections and some cancers. HIV is spread from the body fluids of an infected person, including blood, breast milk, semen and vaginal fluids. WHO now defines Advanced HIV Disease (AHD) as CD4 cell count less than 200 cells/mm3 or WHO stage 3 or 4 in adults and adolescents. All children younger than 5 years of age living with HIV are considered to have advanced HIV disease (WHO, 2024a).', cluster_id = '1844' WHERE id = '78416';
	UPDATE public.hip_hazard SET code= 'BI0205', name_en= 'COVID-19 (SARS-CoV-2)', description_en= 'COVID-19 is an infectious disease caused by the SARS Coronavirus 2 (SARS-CoV-2), a virus first identified in human populations in late 2019 which caused a global outbreak of coronavirus – an infectious disease caused by the severe acute respiratory syndrome coronavirus 2 (SARS-CoV-2) (adapted from WHO, 2023 and WHO Euro, no date).', cluster_id = '1844' WHERE id = '78417';
	UPDATE public.hip_hazard SET code= 'BI0204', name_en= 'Cholera', description_en= 'Cholera is an acute diarrhoeal infection caused by ingestion of food or water contaminated with the bacterium Vibrio cholerae. Cholera is a global threat to public health (WHO, 2024a).', cluster_id = '1844' WHERE id = '78418';
	UPDATE public.hip_hazard SET code= 'BI0111', name_en= 'Cryptosporidium', description_en= 'Cryptosporidium is a microscopic parasite that can live in water, food, soil, or on surfaces that have been contaminated with infected faeces and causes the watery diarrhoeal disease cryptosporidiosis (adapted from CDC 2024 and Peletz et al., 2013).', cluster_id = '1053' WHERE id = '78419';
	UPDATE public.hip_hazard SET code= 'BI0225', name_en= 'Paratyphoid fever', description_en= 'Paratyphoid fever results from systemic infection with Salmonella enterica serotype Paratyphi. It is characterised by febrile illness and, in severe cases, gastrointestinal bleeding, altered mental status, intestinal perforation, and death (IHME 2021).', cluster_id = '1844' WHERE id = '78420';
	UPDATE public.hip_hazard SET code= 'BI0226', name_en= 'Typhoid Fever', description_en= 'Typhoid fever is a life-threatening infection caused by the bacterium Salmonella typhi. It is usually spread through contaminated food or water. As of 2019 estimates, there are 9 million cases of typhoid fever annually, resulting in about 110 000 deaths per year. (WHO, 2023).', cluster_id = '1844' WHERE id = '78421';
	UPDATE public.hip_hazard SET code= 'BI0211', name_en= 'Hepatitis A', description_en= 'Hepatitis A is an acute vaccine-preventable viral liver disease and can cause mild to severe illness. Hepatitis A occurs sporadically and in epidemics worldwide, with a tendency for cyclic recurrences, as the virus persists in the environment and can withstand food production processes routinely used to inactivate or control bacterial pathogens (WHO, 2025a).', cluster_id = '1844' WHERE id = '78422';
	UPDATE public.hip_hazard SET code= 'BI0210', name_en= 'Escherichia Coli (STEC)', description_en= 'Escherichia coli (E. coli) is a bacterium commonly found in the gut. Some strains can cause serious food poisoning, leading to diarrhoea and sometimes to life-threatening complications including haemolytic uraemic syndrome (WHO, 2018a).', cluster_id = '1844' WHERE id = '78423';
	UPDATE public.hip_hazard SET code= 'BI0218', name_en= 'Listeriosis', description_en= 'Listeriosis is a foodborne infection caused by the bacterium Listeria monocytogenes which can be invasive (the more serious form of the disease) or non-invasive (the milder form of the disease). Listeriosis outbreaks occur in all countries and can be a significant public health concern (WHO, 2018).', cluster_id = '1844' WHERE id = '78424';
	UPDATE public.hip_hazard SET code= 'BI0234', name_en= 'Shigellosis', description_en= 'Shigellosis is an acute invasive enteric infection caused by bacteria belonging to the genus Shigella and is estimated to cause at least 80 million cases of bloody diarrhoea and 700,000 deaths each year (WHO, 2005).', cluster_id = '1844' WHERE id = '78425';
	UPDATE public.hip_hazard SET code= 'BI0245', name_en= 'Avian Influenza', description_en= 'Avian influenza is an infectious disease of birds caused by type A influenza viruses of the Orthomyxoviridae family. Naturally occurring among wild bird populations, avian influenza viruses can infect domestic poultry and other bird species. Some avian influenza viruses can also infect mammals and those affecting humans are called zoonotic. A pandemic can occur when a novel influenza virus spreads in human populations worldwide (adapted from FAO, 2009; WHO, 2023a; 2023b; WOAH, no date).', cluster_id = '1844' WHERE id = '78426';
	UPDATE public.hip_hazard SET code= 'BI0244', name_en= 'Pandemic Influenza (Human)', description_en= 'Pandemic influenza is the worldwide spread of a new influenza virus to which there is little or no pre-existing immunity in the human population (WHO, 2019).', cluster_id = '1844' WHERE id = '78427';
	UPDATE public.hip_hazard SET code= 'BI0243', name_en= 'Seasonal Influenza', description_en= 'Seasonal influenza is an acute respiratory infection caused by influenza viruses which circulate in all parts of the world with around a billion cases of seasonal influenza annually, including 3–5 million cases of severe illness. It causes 290,000 to 650,000 respiratory deaths annually (adapted from WHO, 2025).', cluster_id = '1844' WHERE id = '78428';
	UPDATE public.hip_hazard SET code= 'BI0112', name_en= 'Cysticercosis', description_en= 'Cysticercosis is a preventable infection in humans and pigs caused by the larval stages of the parasite Taenia solium (pork tapeworm). Human cysticercosis can result in devastating effects when the larvae are located in the central nervous system, resulting in neurocysticercosis which may cause convulsions and epileptic seizures and can be fatal. It is the main cause of preventable epilepsy where the parasite is present, and it is estimated to affect between 2.56 and 8.30 million people (adapted from WHO, 2023).', cluster_id = '1053' WHERE id = '78429';
	UPDATE public.hip_hazard SET code= 'BI0217', name_en= 'Leptospirosis', description_en= 'Leptospirosis is an infectious disease caused by a pathogenic bacterium of the genus Leptospira. These bacteria called leptospires affect both humans and animals. Humans become infected through direct contact with the urine of infected animals or with a urine-contaminated environment which can lead to serious and sometimes fatal disease (adapted from WHO & ILS, 2003).', cluster_id = '1844' WHERE id = '78430';
	UPDATE public.hip_hazard SET code= 'BI0228', name_en= 'Plague', description_en= 'Plague is caused by the bacteria Yersinia pestis, and can be a very severe disease in people, with a case-fatality ratio of 30% to 60% for the bubonic type and is always fatal for the pneumonic kind when left untreated (WHO, 2022).', cluster_id = '1844' WHERE id = '78431';
	UPDATE public.hip_hazard SET code= 'BI0216', name_en= 'Leprosy', description_en= 'Leprosy is a curable infectious disease, endemic in many countries, caused by the bacterium Mycobacterium leprae (M. leprae). It mainly affects the skin, peripheral nerves, mucosa of the upper respiratory tract and eyes. Untreated, it can lead to permanent disability (adapted from WHO, 2025).', cluster_id = '1844' WHERE id = '78432';
	UPDATE public.hip_hazard SET code= 'BI0203', name_en= 'Chikungunya', description_en= 'Chikungunya is a mosquito-borne viral infection caused by the chikungunya virus. It causes fever and severe arthralgia (joint pain) which is often debilitating. The disease can be endemic and epidemic in countries (WHO, 2025).', cluster_id = '1844' WHERE id = '78433';
	UPDATE public.hip_hazard SET code= 'BI0242', name_en= 'Zika Virus', description_en= 'Zika virus disease is a disease transmitted primarily by Aedes mosquitoes which can lead to complications including microcephaly and other congenital malformations and neurodevelopmental disorders (WHO, 2022a).', cluster_id = '1844' WHERE id = '78434';
	UPDATE public.hip_hazard SET code= 'BI0208', name_en= 'Diphtheria', description_en= 'Diphtheria is a widespread severe infectious disease caused by the bacterium Corynebacterium diphtheriae and the toxin they produce. It is a potentially life-threatening, vaccine-preventable disease that primarily affects the throat and upper airways and has the potential for epidemics (WHO, 2024a).', cluster_id = '1844' WHERE id = '78435';
	UPDATE public.hip_hazard SET code= 'BI0221', name_en= 'Measles', description_en= 'Measles is a highly contagious, serious disease caused by a virus from the paramyxovirus family. It spreads easily when an infected person breathes, coughs or sneezes. It can cause severe disease, complications, and even death (adapted from WHO 2024).', cluster_id = '1844' WHERE id = '78436';
	UPDATE public.hip_hazard SET code= 'BI0222', name_en= 'Meningococcal Meningitis', description_en= 'Meningococcal meningitis is a bacterial form of meningitis, a serious infection of the thin lining that surrounds the brain and spinal cord, that is caused by the bacterium Neisseria meningitidis. Meningococcal meningitis has the potential to cause large-scale epidemics and is observed worldwide (adapted from WHO, 2025).', cluster_id = '1844' WHERE id = '78437';
	UPDATE public.hip_hazard SET code= 'BI0227', name_en= 'Pertussis (Human)', description_en= 'Pertussis is a highly contagious disease, which can be fatal, of the respiratory tract caused by the bacterium Bordetella pertussis (WHO, no date a).', cluster_id = '1844' WHERE id = '78438';
	UPDATE public.hip_hazard SET code= 'BI0229', name_en= 'Polio', description_en= 'Polio (human) is a highly infectious viral disease, which mainly affects young children, where 1 in 200 leads to irreversible paralysis and among those paralysed, 5–10% die when their breathing muscles become immobilized (WHO, 2025).', cluster_id = '1844' WHERE id = '78439';
	UPDATE public.hip_hazard SET code= 'BI0235', name_en= 'Smallpox', description_en= 'Smallpox is an acute contagious disease caused by the variola virus. Before its eradication, smallpox was one of the world’s most devastating diseases known and was fatal in up to 30% of cases (WHO, no date a).', cluster_id = '1844' WHERE id = '78440';
	UPDATE public.hip_hazard SET code= 'BI0239', name_en= 'Varicella and herpes zoster', description_en= 'Varicella is an acute, highly contagious disease caused by varicella- zoster virus. Following infection, the virus remains latent in neural ganglia and in about 10-20% of cases it is reactivated to cause herpes zoster, or shingles, generally in persons over 50 years of age or immunocompromised individuals. (WHO, no date).', cluster_id = '1844' WHERE id = '78441';
	UPDATE public.hip_hazard SET code= 'BI0241', name_en= 'Yellow Fever', description_en= 'Yellow fever is an acute viral haemorrhagic disease transmitted by infected mosquitoes and is a high-impact high-threat disease, with risk of international spread, which represents a potential threat to global health security (WHO, 2023).', cluster_id = '1844' WHERE id = '78442';
	UPDATE public.hip_hazard SET code= 'BI0207', name_en= 'Dengue', description_en= 'Dengue is a mosquito-borne disease that is caused by a virus of the Flaviviridae family and transmitted by female mosquitoes mainly of the species Aedes aegypti and, to a lesser extent, A. albopictus. The incidence of dengue has grown dramatically around the world in recent decades, with cases reported to WHO increasing from 505 430 cases in 2000 to 5.2 million in 2019 (adapted from WHO, 2024).', cluster_id = '1844' WHERE id = '78443';
	UPDATE public.hip_hazard SET code= 'BI0219', name_en= 'Malaria', description_en= 'Malaria is a life-threatening disease caused by parasites that are transmitted to people mostly through the bites of infected female Anopheles mosquitoes. In 2023, there were an estimated 263 million cases of malaria worldwide and the estimated number of malaria deaths stood at 597,000 in 83 countries (adapted from WHO, 2024a).', cluster_id = '1844' WHERE id = '78444';
	UPDATE public.hip_hazard SET code= 'BI0206', name_en= 'Crimean-Congo Haemorrhagic Fever', description_en= 'Crimean-Congo haemorrhagic fever (CCHF) is a tick-borne viral infection caused by the CCHF virus. It causes severe viral haemorrhagic fever outbreaks and epidemics. CCHF outbreaks have a case fatality rate of 10-40% (WHO, 2025).', cluster_id = '1844' WHERE id = '78445';
	UPDATE public.hip_hazard SET code= 'BI0209', name_en= 'Ebola', description_en= 'Ebola virus disease (EVD) is a rare but severe zoonotic viral infectious disease caused by the Ebola virus. It can be characterized by haemorrhagic fever and is often fatal in humans. EVD can trigger epidemics with high case-fatality rates (WHO, 2025).', cluster_id = '1844' WHERE id = '78446';
	UPDATE public.hip_hazard SET code= 'BI0215', name_en= 'Lassa Fever', description_en= 'Lassa fever is a zoonotic disease associated with acute and potentially fatal haemorrhagic illness caused by Lassa virus. The virus is a single-stranded RNA virus belonging to the virus family Arenaviridae. About 1 in 5 infections result in severe disease, where the virus affects several organs such as the liver, spleen and kidneys. (WHO, 2024a).', cluster_id = '1844' WHERE id = '78447';
	UPDATE public.hip_hazard SET code= 'BI0237', name_en= 'Tuberculosis', description_en= 'Tuberculosis (TB) is a curable bacterial infectious disease caused by Mycobacterium tuberculosis that most commonly affects the lungs. It causes national epidemics of varied severity worldwide. Forms of TB that are resistant to treatment - multi-drug-resistant TB (MDR-TB) and extensively drug-resistant TB (XDR-TB) - are public health crises and threaten health security worldwide (WHO, 2024).', cluster_id = '1844' WHERE id = '78448';
	UPDATE public.hip_hazard SET code= 'BI0223', name_en= 'Middle East Respiratory Syndrome (MERS)', description_en= 'Middle East respiratory syndrome (MERS) is a viral respiratory disease caused by Middle East respiratory syndrome coronavirus (MERS‐CoV) with a clinical spectrum of infection ranging from no symptoms (asymptomatic) or mild respiratory symptoms to severe acute respiratory disease and death (adapted from WHO, 2022).', cluster_id = '1844' WHERE id = '78449';
	UPDATE public.hip_hazard SET code= 'BI0224', name_en= 'Mpox', description_en= 'Mpox, previously known as monkeypox, is a viral illness caused by the monkeypox virus, a species of the genus Orthopoxvirus. There are two distinct clades of the virus: clade I (with subclades Ia and Ib) and clade II (with subclades IIa and IIb). Mpox can be fatal in some cases (adapted from WHO, 2024).', cluster_id = '1844' WHERE id = '78450';
	UPDATE public.hip_hazard SET code= 'BI0232', name_en= 'Rabies', description_en= 'Rabies is a serious public health problem in over 150 countries and territories. It is a viral zoonotic disease that affects the central nervous system and spreads from bites, licks, and scratches from infected mammals. It is almost always fatal when clinical signs appear, killing approximately 59,000 people every year (adapted from WHO, 2024a; and WOAH no date).', cluster_id = '1844' WHERE id = '78451';
	UPDATE public.hip_hazard SET code= 'BI0236', name_en= 'Severe Acute Respiratory Syndrome (SARS)', description_en= 'Severe acute respiratory syndrome (SARS) is a viral respiratory illness caused by a coronavirus called SARS-associated coronavirus (SARS-CoV). Among individuals who meet the current WHO case definition for confirmed SARS, the case-fatality rate is approximately 9.6% (WHO, no date).', cluster_id = '1844' WHERE id = '78452';
	UPDATE public.hip_hazard SET code= 'BI0233', name_en= 'Rotavirus', description_en= 'Rotaviruses are the most common cause of severe diarrhoeal disease in young children throughout the world. According to WHO estimates in 2013 about 215,000 children aged under 5 years die each year from vaccine-preventable rotavirus infections; the vast majority of these children live in low-income countries (WHO, 2018).', cluster_id = '1844' WHERE id = '78453';
	UPDATE public.hip_hazard SET code= 'BI0202', name_en= 'Brucellosis', description_en= 'Brucellosis is a bacterial disease caused by various Brucella species, which mainly infect cattle, swine, goats, sheep and dogs. Humans generally acquire the disease through direct contact with infected animals (WHO, 2020).', cluster_id = '1844' WHERE id = '78455';
	UPDATE public.hip_hazard SET code= 'BI0304', name_en= 'Contagious Bovine Pleuropneumonia (CBPP)', description_en= 'Contagious bovine pleuropneumonia (CBPP) is an infectious and contagious respiratory disease of cattle and water buffalo caused by Mycoplasma mycoides subsp. mycoides (Mmm) with a major impact on livestock production and a potential for rapid spread (WOAH, 2024a).', cluster_id = '1054' WHERE id = '78456';
	UPDATE public.hip_hazard SET code= 'BI0305', name_en= 'Contagious Caprine Pleuropneumonia (CCPP)', description_en= 'Contagious caprine pleuropneumonia (CCPP) is a disease affecting goats and some wild ruminant species, caused by Mycoplasma capricolum subsp. capripneumoniae (Mccp). In goats, it is manifested by anorexia, fever and respiratory signs such as dyspnoea, polypnea, cough and nasal discharges. The acute and subacute disease is characterised by unilateral serofibrinous pleuropneumonia with severe pleural effusion. (Adapted from WOAH, 2021).', cluster_id = '1054' WHERE id = '78457';
	UPDATE public.hip_hazard SET code= 'BI0306', name_en= 'Foot-and-mouth disease', description_en= 'Foot-and-mouth disease is caused by a virus of the family Picornaviridae, genus Aphthovirus. It is a highly contagious and economically important disease of cloven-hoofed domestic animals (cattle, buffaloes, pigs, sheep, goats) and wild animals (adapted from FAO no date; WOAH, no date).', cluster_id = '1054' WHERE id = '78458';
	UPDATE public.hip_hazard SET code= 'BI0307', name_en= 'Lumpy Skin Disease', description_en= 'Lumpy skin disease is a vector-borne pox disease of domestic cattle and Asian water buffalo and is characterised by the appearance of skin nodules on all body surfaces including the udder (FAO, 2017).', cluster_id = '1054' WHERE id = '78459';
	UPDATE public.hip_hazard SET code= 'BI0309', name_en= 'New World Screwworm (NWS)', description_en= 'The New World screwworm (NWS), Cochliomyia hominivorax (Coquerel), is an obligate parasite of mammals, including humans, during their larval stages. Larvae feeding on the skin and underlying tissues of the host cause a condition known as wound or traumatic myiasis, which can be fatal (adapted from PAHO, no date and WOAH, no date).', cluster_id = '1054' WHERE id = '78460';
	UPDATE public.hip_hazard SET code= 'BI0308', name_en= 'Newcastle Disease', description_en= 'Newcastle Disease (ND) is a highly contagious and often severe disease found worldwide that affects birds caused by virulent strains of avian paramyxovirus type 1 (WOAH, no date).', cluster_id = '1054' WHERE id = '78461';
	UPDATE public.hip_hazard SET code= 'BI0310', name_en= 'Peste Des Petits Ruminants', description_en= 'Peste des petits ruminants is a highly contagious and devastating disease of goats and sheep. The causative agent, Peste des petits ruminants virus, is a member of the genus Morbillivirus, Family Paramyxoviridae and Order Mononegavirales (adapted from FAO, no date and WOAH, 2024a).', cluster_id = '1054' WHERE id = '78462';
	UPDATE public.hip_hazard SET code= 'BI0231', name_en= 'Q Fever', description_en= 'Q fever is a common zoonosis (infection that could transmit from animals to humans), caused by Coxiella burnetii. Natural reservoirs include several domestic and wild animals, most of which show no signs of disease (although infection can cause abortions). Due to the high resilience in the environment of Coxiella, humans are most often infected by inhalation of aerosols produced in contaminated locations, but other modes of infection have been documented (including food-borne) (ECDC, no date).', cluster_id = '1844' WHERE id = '78463';
	UPDATE public.hip_hazard SET code= 'BI0311', name_en= 'Rift Valley Fever', description_en= 'Rift Valley fever (RVF) is an acute haemorrhagic viral disease, affecting small and large ruminants and camels. RVF virus is a member of the Phlebovirus genus. The disease causes high mortality, especially in newborns and mass abortions in pregnant animals. Humans become infected from contact with tissues/blood of infected animals including abortive material and through mosquito bites. Disease in humans presents as influenza-like illness, haemorrhagic fever, encephalitis and occasionally death (adapted from FAO, 2003; WHO, 2018; OIE 2020).', cluster_id = '1054' WHERE id = '78464';
	UPDATE public.hip_hazard SET code= 'BI0240', name_en= 'West Nile Fever', description_en= 'West Nile virus disease is a potentially fatal neurological disease caused by a virus transmitted through the bites of infected mosquitoes. The virus is a member of the flavivirus genus and belongs to the Japanese encephalitis antigenic complex of the family Flaviviridae (WHO, 2017).', cluster_id = '1844' WHERE id = '78466';
	UPDATE public.hip_hazard SET code= 'BI0238', name_en= 'Trypanosomiasis', description_en= 'Trypanosomiasis, human African (sleeping sickness) and animal trypanosomosis (nagana) are caused by protozoan parasites belonging to the genus Trypanosoma transmitted by infected tsetse flies and is endemic in sub-Saharan Africa. Without treatment, the disease is considered fatal (adapted from WHO, 2023 and FAO, no date).', cluster_id = '1844' WHERE id = '78467';
	UPDATE public.hip_hazard SET code= 'BI0302', name_en= 'African Swine Fever', description_en= 'African swine fever (ASF) is a highly contagious viral disease of domestic and wild pigs, whose mortality rate can reach 100%. It is not a danger to human health, but it has devastating effects on pig populations and the farming economy. (WOAH, no date).', cluster_id = '1054' WHERE id = '78468';
	UPDATE public.hip_hazard SET code= 'BI0303', name_en= 'Classical Swine Fever', description_en= 'Classical swine fever, also known as hog cholera, is a contagious viral disease of domestic and wild swine. It is caused by a virus of the genus Pestivirus of the family Flaviviridae (WOAH, no date).', cluster_id = '1054' WHERE id = '78469';
	UPDATE public.hip_hazard SET code= 'BI0312', name_en= 'Rinderpest', description_en= 'Rinderpest was an acute, highly contagious viral disease of wild and domesticated ruminants and pigs, characterized by sudden onset of fever, oculonasal discharges, necrotic stomatitis, gastroenteritis and death. Rinderpest was eradicated in 2011 (FAO & OIE, 2011).', cluster_id = '1054' WHERE id = '78470';
	UPDATE public.hip_hazard SET code= 'BI0314', name_en= 'Shrimp disease (bacterial) - Acute Hepatic Pancreatic Necrosis', description_en= 'Shrimp acute hepatopancreatic necrosis disease (AHPND) is caused by virulent strains of Vibrio parahaemolyticus and related Vibrio species. AHPND-associated mortalities occur early in the production cycle, usually within 30 to 35 days of stocking, and because of this AHPND was initially referred to as early mortality syndrome (WOAH, 2023).', cluster_id = '1054' WHERE id = '78471';
	UPDATE public.hip_hazard SET code= 'BI0313', name_en= 'Oyster Disease Aquaculture', description_en= 'There are a number of causal agents recognised for oyster diseases. Examples of major oyster diseases and their causal protozoan agents are: bonamiosis (Bonamia exitiosa, B. ostreae); marteiliosis (Marteilia refringens); perkinsosis (Perkinsus marinus, P. olseni). These oyster diseases are notifiable OIE-listed diseases and occur worldwide (WOAH, 2024a).', cluster_id = '1054' WHERE id = '78472';
	UPDATE public.hip_hazard SET code= 'CH0301', name_en= 'Ammonia', description_en= 'Ammonia (NH3) is a colourless acrid-smelling reactive gas at ambient temperature and pressure and is considered a significant public health hazard (WHO, 1986; PHE, 2019).', cluster_id = '1056' WHERE id = '78473';
	UPDATE public.hip_hazard SET code= 'CH0302', name_en= 'Carbon Monoxide', description_en= 'Carbon monoxide is a colourless, odourless gas that can be poisonous to humans and is considered a significant public health hazard (WHO, 1999).', cluster_id = '1056' WHERE id = '78474';
	UPDATE public.hip_hazard SET code= 'CH0303', name_en= 'Chlorine', description_en= 'Chlorine is a reactive pale green gas with many uses including disinfection of water. Chlorine is approximately 2.4 times heavier than air and has a characteristic odour similar to bleach. Most significant exposures to chlorine result from loss of containment of chlorine during storage and transport. Human exposure can result in symptoms ranging from mild irritation to rapid death related to pulmonary oedema. It is considered a significant public health hazard (adapted from IPCS, 1982 and PHE, 2019).', cluster_id = '1056' WHERE id = '78476';
	UPDATE public.hip_hazard SET code= 'CH0101', name_en= 'Arsenic', description_en= 'Arsenic is a toxic metalloid widely distributed throughout the Earth’s crust, generally as arsenic sulfide or as metal arsenates and arsenides. Human exposure to arsenic compounds represents a major public health concern as it has been associated with a range of acute and long-term adverse health effects and diseases (WHO, 2019).', cluster_id = '1057' WHERE id = '78477';
	UPDATE public.hip_hazard SET code= 'CH0102', name_en= 'Cadmium', description_en= 'Cadmium is a toxic heavy metal which is widely distributed in the Earth’s crust (soil and rocks), air and water; however, human activity has greatly increased levels in environmental media relevant to population exposure. Human exposure to cadmium represents a major public health concern as it has been associated with a range of acute and long-term adverse health effects and diseases (WHO, 2019).', cluster_id = '1057' WHERE id = '78478';
	UPDATE public.hip_hazard SET code= 'CH0103', name_en= 'Lead', description_en= 'Lead is a naturally occurring highly toxic heavy metal. Its widespread use has caused extensive environmental contamination and health problems in many parts of the world. It is a cumulative toxicant that affects multiple body systems, including the neurological, haematological, gastrointestinal, cardiovascular and renal systems. Children are particularly vulnerable to the neurotoxic effects of lead, and even relatively low levels of exposure can cause serious and, in some cases, irreversible neurological damage (WHO, 2024).', cluster_id = '1057' WHERE id = '78479';
	UPDATE public.hip_hazard SET code= 'CH0104', name_en= 'Mercury', description_en= 'Mercury is a naturally occurring element that is found in air, water and soil. Exposure to mercury – even small amounts – may cause serious health problems and is a threat to the development of the foetus in utero and for children early in life (WHO, 2021).', cluster_id = '1057' WHERE id = '78480';
	UPDATE public.hip_hazard SET code= 'CH0601', name_en= 'Levels of Contaminants in Food and Feed', description_en= 'A contaminant in food and feed is defined as any substance not intentionally added but present in such food or feed as a result of the production, manufacture, processing, preparation, treatment, packing, packaging, transport or storage, or as a result of environmental contamination, which can lead to major public hazards (FAO and WHO, 2019).', cluster_id = '1058' WHERE id = '78481';
	UPDATE public.hip_hazard SET code= 'CH0501', name_en= 'Pesticides', description_en= 'Pesticide means any substance, or mixture of substances of chemical or biological ingredients intended for repelling, destroying or controlling any pest, or regulating plant growth.', cluster_id = '1060' WHERE id = '78482';
	UPDATE public.hip_hazard SET code= 'CH0502', name_en= 'Dioxins and Dioxin-like Substances', description_en= 'Dioxins and dioxin-like substances, including polychlorinated biphenyls (PCBs), polychlorinated dibenzo-p-dioxins (PCDDs) and polychlorinated dibenzofurans (PCDFs) are ​​​​persistent organic pollutants (POPs, see CH0​500​) and are unwanted by-products of combustion and various industrial processes, such as chlorine bleaching of paper pulp and smelting. They can travel long distances from the source of emission, and bioaccumulate in food chains. These substances represent a major public health concern. They have been associated with a range of acute and long-term adverse health effects and diseases (WHO, 2019).', cluster_id = '1060' WHERE id = '78487';
	UPDATE public.hip_hazard SET code= 'CH0504', name_en= 'Microplastics', description_en= 'Microplastics are small plastic pieces less than five millimetres in length which can be harmful to the environment, especially marine life. They originate from a variety of sources, including larger plastic debris that degrade into progressively smaller pieces (adapted from UNEP, 2016 and NOAA, 2023).', cluster_id = '1060' WHERE id = '78488';
	UPDATE public.hip_hazard SET code= 'CH0203', name_en= 'Benzene and Hydrocarbons', description_en= 'Hydrocarbons are organic compounds composed entirely of hydrogen (H) and carbon (C) atoms. They are the simplest form of organic molecules and are the main components of fossil fuels such as coal, natural gas, and petroleum. Benzene is a clear, colourless, highly flammable and volatile, liquid aromatic hydrocarbon (molecular formula C6H6) with a gasoline-like odour, which can lead to major public hazards. Benzene is the simplest aromatic hydrocarbon, characterised by alternating single and double bonds between the carbon atoms, forming a delocalized π-electron system. (WHO, 2019) The release of hydrocarbon ground gases, such as methane, can lead to major public hazards.', cluster_id = '1061' WHERE id = '78490';
	UPDATE public.hip_hazard SET code= 'CH0903', name_en= 'Chemical Warfare Agents', description_en= 'Chemical agents or ‘chemical warfare agents’ (chemical weapons) are chemicals, including dual -use chemicals, used to cause intentional death or harm through their toxic properties, and are a major public hazard. (OPCW, 2024a, b, c).', cluster_id = '1063' WHERE id = '78491';
	UPDATE public.hip_hazard SET code= 'CH0202', name_en= 'Asbestos', description_en= 'Asbestos is the term for a group of naturally occurring fibrous silicate minerals widely used historically in building materials and other products (WHO, 2018). All types of asbestos cause lung cancer, mesothelioma, cancer of the larynx and ovary, and asbestosis (fibrosis of the lungs) (WHO, 2018).', cluster_id = '1061' WHERE id = '78492';
	UPDATE public.hip_hazard SET code= 'CH0201', name_en= 'Aflatoxins', description_en= 'Aflatoxins are mycotoxins – toxic compounds that are naturally produced by certain types of mould (fungi). Aflatoxins are among the most poisonous mycotoxins and are produced by certain moulds (Aspergillus flavus and A. parasiticus) that grow in soil, decaying vegetation, hay, and grains. Aflatoxins pose a serious health risk to humans and livestock (WHO, 2023).', cluster_id = '1061' WHERE id = '78493';
	UPDATE public.hip_hazard SET code= 'CH0105', name_en= 'Fluoride and Iodine/Iodide Excess or Inadequate Intake', description_en= 'Fluoride is a naturally occurring mineral to which the public are often exposed via drinking water. Depending on dose intake, fluoride may have both beneficial effects (reducing the incidence of dental caries) or negative effects (causing tooth enamel and skeletal fluorosis following prolonged high exposure) (adapted from NCBI, 2020 and WHO, no date). Some water supplies are fluoridated in order to achieve improved dental health.Iodine is a non-metallic element essential for the human body, as it is a crucial component of thyroid hormones, which regulate various metabolic processes, including growth and energy expenditure. Iodine, usually as iodide salts in the diet, is absorbed throughout the gastrointestinal tract. Iodine is essential for healthy brain development in the foetus and young child. A deficiency in iodine can lead to thyroid gland problems, such as goitre and hypothyroidism. Iodine deficiency negatively affects the health of women, as well as economic productivity and quality of life (WHO, 2023). Food fortification (often with potassium iodide, KI) is sometimes used to address iodine deficiency.', cluster_id = '1057' WHERE id = '78494';
	UPDATE public.hip_hazard SET code= 'CH0603', name_en= 'Methanol', description_en= 'Methanol is a colourless, volatile liquid. Categorized as an alcohol, methanol is commonly used as a solvent and reagent in an array of industrial applications. Outbreaks of methanol poisoning most commonly arise from the consumption of adulterated or informally produced spirit drinks (adapted from NCBI, 2024 and WHO, 2014).', cluster_id = '1058' WHERE id = '78495';
	UPDATE public.hip_hazard SET code= 'CH0602', name_en= 'Substandard and Falsified Medical Products', description_en= 'Substandard and falsified medical products are defined as those that may cause harm to patients and fail to treat the diseases for which they were intended (WHO, 2018).', cluster_id = '1058' WHERE id = '78496';
	UPDATE public.hip_hazard SET code= 'CH0605', name_en= 'Marine Toxins', description_en= 'Marine toxins (biotoxins) are naturally occurring, toxic substances, mostly caused by certain types of marine organisms such as toxic algae, but also by bacteria. These toxins can accumulate in fish and shellfish, causing significant public health concerns due to their potential to cause a wide range of adverse health effects.', cluster_id = '1058' WHERE id = '78497';
	UPDATE public.hip_hazard SET code= 'EN0101', name_en= 'Household Air Pollution', description_en= 'Household air pollution is pollution primarily resulting from the incomplete combustion of solid fuels (e.g. wood, dung, charcoal, coal, kerosene), resulting in the emission of potentially toxic pollutants, including particles of varying sizes, carbon monoxide (CO), nitrogen dioxide, volatile and semi-volatile organic compounds (e.g. formaldehyde and benzo[a]pyrene), methylene chloride and dioxins. It is one of the leading environmental risk factors for disease and premature death and is generated by the use of inefficient and polluting fuels and technologies in and around homes.', cluster_id = '1065' WHERE id = '78498';
	UPDATE public.hip_hazard SET code= 'EN0102', name_en= 'Air Pollution (Point Source)', description_en= 'A point source of air pollution is an identifiable stationary location or fixed facility from which air pollutants are released, which may be human-made or natural in origin (adapted from Kibble and Harrison, 2005; Dunne et al., 2014).', cluster_id = '1065' WHERE id = '78499';
	UPDATE public.hip_hazard SET code= 'EN0103', name_en= 'Ambient (Outdoor) Air Pollution', description_en= 'Ambient (outdoor) air pollution is pollution that is present at concentrations that affect human health, ecosystems and agriculture. It is primarily measured through the presence of particulate matter (PM10 and PM2.5), ozone, nitrogen dioxide, sulphur dioxide and carbon monoxide in the air. Ambient air pollution is one of the leading environmental risk factors affecting urban and rural populations around the world, resulting in an estimated 4.2 million premature deaths in 2019 (WHO, 2024).', cluster_id = '1065' WHERE id = '78500';
	UPDATE public.hip_hazard SET code= 'EN0301', name_en= 'Land Degradation', description_en= 'Land degradation is the reduction or loss, in arid, semi-arid and dry sub-humid areas, of the biological or economic productivity and complexity of rainfed cropland, irrigated cropland or range, pasture, forest and woodlands resulting from land uses or from a process or combination of processes, including processes arising from human activities and habitation patterns such as soil erosion caused by wind and/or water; deterioration of the physical, chemical and biological or economic properties of soil; and long-term loss of natural vegetation (UNCCD, 1993).', cluster_id = '1065' WHERE id = '78501';
	UPDATE public.hip_hazard SET code= 'GH0402', name_en= 'Soil Degradation', description_en= 'Soil degradation is defined as a change in soil health status resulting in a diminished capacity of the ecosystem to provide goods and services for its beneficiaries (FAO, 2020).', cluster_id = '1070' WHERE id = '78502';
	UPDATE public.hip_hazard SET code= 'EN0106', name_en= 'Runoff / Nonpoint Source Pollution', description_en= 'Nonpoint sources of pollution refer to pollution that does not have a single point of origin or has not been introduced into a receiving freshwater or maritime environment from a specific outlet. The pollutants are generally carried off from the land by agricultural runoff, urban stormwater, atmospheric deposition or subaqueous groundwater discharges. The most common categories of nonpoint pollution are agriculture, forestry, urban areas, mining, construction, dams and channels, land disposal and saltwater intrusion.', cluster_id = '1065' WHERE id = '78503';
	UPDATE public.hip_hazard SET code= 'EN0303', name_en= 'Salinity &amp; Sodicity', description_en= 'Salt-affected soils consist of saline and sodic soils. Saline soils are those with an elevated amount of soluble salts, which reduces the ability of plants to take up water from soil due to the high osmotic pressure of the soil solution (FAO, 1985).The technical criteria used to distinguish saline soil from other soils is the electrical conductivity (ECe) of a soil paste saturation extract: ECe > 2 dS/m (slightly saline) or ECe > 4 dS/m (saline) at 25 C. The content of soluble salts should be higher than 0.1-0.2% (FAO, 2018). The threshold of salinity above which a plant will suffer deleterious effects varies according to plant species, type of ions in solution, soil health and soil fertility status.Sodic soils get their name from sodium ions (Na⁺) adsorbed on soil clays and organic matter. Sodic soils have elevated amounts of exchangeable Na⁺ compared to the amounts of Ca²⁺ and Mg²⁺, measured as sodium adsorption ratio (SAR) > 13 or exchangeable sodium percentage (ESP) > 15, and with relatively lower salinity (ECe', cluster_id = '1065' WHERE id = '78504';
	UPDATE public.hip_hazard SET code= 'EN0501', name_en= 'Biodiversity Loss', description_en= 'Biodiversity loss refers to the reduction of any aspect of biological diversity (i.e. diversity at the genetic, species and ecosystem levels) in a particular area through death (including extinction), destruction or manual removal. It can occur at many scales, from global extinctions to local population extinctions, leading to a decline in total diversity at the same scale.', cluster_id = '1065' WHERE id = '78505';
	UPDATE public.hip_hazard SET code= 'EN0202', name_en= 'Forest Declines and Diebacks', description_en= 'Forest declines and diebacks are episodic events characterised by premature, progressive loss of tree and stand vigour and health over a given period without obvious evidence of a single clearly identifiable causal factor such as physical disturbance or attack by primary disease or insect (Ciesla & Donaubauer, 1994).Tree declines can be described as the gradual deterioration of plant tissues over time, triggered by a series of adverse events such as abiotic stress, climate deregulation, the emergence of new pathogens, biological invasions and agricultural strategies. Declines may also be seen as a long-term reduction of wood or fruit productivity, leading (or not) to sudden tree mortality, occasionally referred to as dieback (Bettenfeld et al., 2020).', cluster_id = '1065' WHERE id = '78506';
	UPDATE public.hip_hazard SET code= 'EN0203', name_en= 'Forest Disturbances', description_en= 'Forest disturbance is the damage caused by any factor (biotic or abiotic) that adversely affects the vigour and productivity of the forest, and which is not a direct result of human activities. It includes disturbance by insect pests, diseases, severe weather events and fires (FAO, 2018; 2020).', cluster_id = '1065' WHERE id = '78507';
	UPDATE public.hip_hazard SET code= 'EN0206', name_en= 'Desertification', description_en= 'Desertification refers to land degradation in arid, semi-arid and dry sub-humid areas resulting from various factors, including climatic variations and human activities (UNCCD, 2017).', cluster_id = '1065' WHERE id = '78508';
	UPDATE public.hip_hazard SET code= 'EN0207', name_en= 'Loss of Mangroves', description_en= 'Mangroves are distinctive tropical and sub-tropical, woody plants that grow at the interface/intertidal zone between land and sea, where they exist in conditions of high salinity, extreme tides, strong winds, high temperatures and muddy, anaerobic soils (Kathiresan and Bingham, 2001). The destruction of mangrove habitat is caused by both human and natural causes. Humans have cleared mangrove forests to expand farmlands, aquaculture ponds or urban areas. Natural stressors, such as sediment erosion, extreme storm surges or drought have also resulted in mangrove habitat loss. The loss of mangroves has devastated coastal communities, which depend on them for socio-economic activities and environmental conservation, especially in regions with low mangrove diversity and coverage.', cluster_id = '1065' WHERE id = '78509';
	UPDATE public.hip_hazard SET code= 'EN0304', name_en= 'Wetland Loss/Degradation', description_en= 'Wetland loss/degradation is a negative trend in wetland condition, caused by physical or direct/indirect human-induced processes, expressed as a long-term reduction or loss of at least one of the following: biological productivity, ecological role or value to humans (Olsson et al., 2019). Wetlands are defined as areas of marsh, fen, peatland or water, whether natural or artificial, permanent or temporary, with water that is static or flowing, fresh, brackish or salt, including areas of marine water the depth of which at low tide does not exceed six metres (Convention on Wetlands, 1971: Article 1.1). Wetlands may incorporate riparian and coastal zones adjacent to the wetlands, and islands or bodies of marine water deeper than six metres at low tide lying within the wetlands (Convention on Wetlands, 1971: Article 2.1).', cluster_id = '1065' WHERE id = '78510';
	UPDATE public.hip_hazard SET code= 'EN0404', name_en= 'Coral Bleaching', description_en= 'Corals are subject to ‘bleaching’ when the seawater temperature is too high: they lose the symbiotic algae that give coral its colour and part of its nutrients. Severe, prolonged or repeated bleaching can lead to the death of coral colonies (United Nations, 2017).', cluster_id = '1065' WHERE id = '78511';
	UPDATE public.hip_hazard SET code= 'GH0401', name_en= 'Compressive Soils', description_en= 'Compressive soils are prone to volumetric change when subject to mechanical loading (USDA, 1990:30). Collapsible soils are metastable in that they are prone to volumetric change (collapse) on wetting and loading (Rogers, 1995).', cluster_id = '1070' WHERE id = '78512';
	UPDATE public.hip_hazard SET code= 'GH0403', name_en= 'Soil Erosion', description_en= 'Erosion is the wearing away of the land surface by water, wind, ice, gravity or other natural or anthropogenic agents that abrade, detach and remove soil particles from one point on the earth''s surface, for deposition elsewhere. Four main forms are recognized: water, wind, harvest and tillage. (FAO, 2020).', cluster_id = '1070' WHERE id = '78513';
	UPDATE public.hip_hazard SET code= 'GH0405', name_en= 'Coastal Erosion and Accretion', description_en= 'Coastal erosion is the process of removal of material at the shoreline which leads to the loss of land as the shoreline retreats landward. Coastal accretion is the product of deposition of material at the shoreline which leads to gain of land as the coast advances seaward (Gibb, 1978).', cluster_id = '1070' WHERE id = '78514';
	UPDATE public.hip_hazard SET code= 'EN0305', name_en= 'Permafrost Loss', description_en= 'Permafrost is defined as ground that remains frozen at or below 0°C for a minimum of two consecutive years. Permafrost loss, also known as permafrost thaw, is the progressive loss of ground ice in permafrost, usually due to input of heat. Thaw can occur over decades to centuries over the entire depth of permafrost ground, with impacts occurring while thaw progresses. During thaw, temperature fluctuations are subdued because energy is transferred by phase change between ice and water. After the transition from permafrost to non-permafrost, ground can be described as thawed (IPCC, 2019).', cluster_id = '1065' WHERE id = '78515';
	UPDATE public.hip_hazard SET code= 'EN0405', name_en= 'Sand Mining', description_en= 'Sand mining (extraction) is defined as the removal of primary (virgin) natural sand and sand resources (mineral sands and aggregates) from the natural environment (terrestrial, riverine, coastal, or marine) for extracting valuable minerals, metals, crushed stone, sand and gravel for subsequent processing (UNEP, 2019).', cluster_id = '1065' WHERE id = '78516';
	UPDATE public.hip_hazard SET code= 'EN0402', name_en= 'Sea Level Rise', description_en= 'Sea-level change (sea-level rise / sea-level fall) refers to a change in the height of sea level, both globally and locally (relative sea-level change), at seasonal, annual, or longer time scales. It results from a change in ocean volume due to a change in the mass of water in the ocean (e.g. melting of glaciers and ice sheets), changes in ocean water density (e.g. expansion under warmer conditions), changes in the shape of ocean basins, changes in the Earth''s gravitational and rotational fields, and local land subsidence or uplift (IPCC, 2019).', cluster_id = '1065' WHERE id = '78517';
	UPDATE public.hip_hazard SET code= 'EN0403', name_en= 'Eutrophication', description_en= 'Eutrophication refers to the phenomenon of increased production of organic matter, primarily nitrogen and phosphorus, in aquatic systems (Nixon, 1995). Eutrophication can be caused by human activities (e.g. sewage outfall, agricultural runoff, aquaculture) and may result in secondary environmental effects such as algal blooms and fish kills (NOAA, 2007; UNEP, 2015). Given the complex structure and functioning of ecosystems, and the multitude of pressures they face (Cloern, 2001), the precise definition of eutrophication remains to be established (Pannard et al., 2024).', cluster_id = '1065' WHERE id = '78518';
	UPDATE public.hip_hazard SET code= 'EN0201', name_en= 'Deforestation', description_en= 'Deforestation is the conversion of forest to other land use independently of whether human-induced or not (FAO, 2023).', cluster_id = '1065' WHERE id = '78519';
	UPDATE public.hip_hazard SET code= 'EN0204', name_en= 'Forest Invasive Species', description_en= 'Forest invasive species are any species that are non-native to a particular forest ecosystem and whose introduction and spread cause, or are likely to cause, socio-cultural, economic or environmental harm, or harm to human health (adapted from FAO, 2015).', cluster_id = '1065' WHERE id = '78520';
	UPDATE public.hip_hazard SET code= 'EN0205', name_en= 'Wildfires', description_en= 'Any unplanned and uncontrolled vegetation fire that, regardless of ignition source, may negatively affect social, economic or environmental values, and require suppression response or other action according to agency policy (FAO, 2024).', cluster_id = '1065' WHERE id = '78521';
	UPDATE public.hip_hazard SET code= 'ET0201', name_en= 'Airburst', description_en= 'An airburst is defined as a high-energy explosion in mid-air caused either by an artificial explosive device or by the sudden disruption of a natural object entering the Earth''s atmosphere at high speeds at an altitude where the hydrodynamic pressure exceeds the structural integrity of the object (adapted from ESA, No date).An airburst is defined as an explosion in the air, especially of a nuclear bomb or large meteorite (Lexico Dictionary, no date).', cluster_id = '1067' WHERE id = '78522';
	UPDATE public.hip_hazard SET code= 'ET0101', name_en= 'Geomagnetic Disturbance', description_en= 'A geomagnetic disturbance refers to perturbations in Earth''s magnetosphere caused by sudden and significant variations in the solar wind''s speed, density, and magnetic properties. The intensity of a geomagnetic disturbance can be measured using different geomagnetic indexes. Although a geomagnetic disturbance is a global phenomenon, the intensities and characteristics vary at different geographic locations (NOAA, 2023).', cluster_id = '1833' WHERE id = '78523';
	UPDATE public.hip_hazard SET code= 'ET0202', name_en= 'Ultraviolet Radiation', description_en= 'Ultraviolet radiation (UVR) exposure, either from the sun or from artificial sources such as sunbeds, is primarily responsible for skin cancers. Globally in 2020, over 1.5 million cases of skin cancers were diagnosed and over 120, 000 skin cancer-associated deaths were reported. (WHO 2022a).', cluster_id = '1067' WHERE id = '78524';
	UPDATE public.hip_hazard SET code= 'ET0203', name_en= 'Meteorite Impact', description_en= 'A meteorite is an object that survives a trip through Earth’s atmosphere and hits the ground (adapted from NASA, no date).', cluster_id = '1067' WHERE id = '78525';
	UPDATE public.hip_hazard SET code= 'ET0102', name_en= 'Ionospheric Disturbances', description_en= 'Ionospheric disturbances refer to the state of the ionosphere characterized by irregular variations of ionospheric parameters without a systematic pattern. These disturbances can be of different spatiotemporal scales and have distinct characteristics at different geographic locations. Different ionospheric disturbances pose different risks to various applications (WMO, 1992).', cluster_id = '1833' WHERE id = '78526';
	UPDATE public.hip_hazard SET code= 'ET0103', name_en= 'Solar Flares', description_en= 'A solar flare is a sudden and large explosion on the Sun characterized by the rapid release of energy, resulting in the emission of electromagnetic radiation across all wavelengths and a rapid increase in brightness on a portion of the Sun''s surface. The sudden outburst of electromagnetic energy travels at the speed of light therefore any effect upon the sunlit side of Earth’s exposed outer atmosphere occurs at the same time the event is observed (NOAA Space Weather Prediction Center, 2023).', cluster_id = '1833' WHERE id = '78527';
	UPDATE public.hip_hazard SET code= 'ET0104', name_en= 'Solar Energetic Particles', description_en= 'Solar energetic particle (SEP) events occur when a large-scale magnetic eruption, often accompanied by a coronal mass ejection and/or a related solar flare, accelerates charged particles in the solar atmosphere to significant fractions of the speed of light. The primary particles of interest are protons. SEP arrive with diverse fluxes and energies at different geographic locations (NOAA, 2023).', cluster_id = '1833' WHERE id = '78528';
	UPDATE public.hip_hazard SET code= 'ET0204', name_en= 'Space Hazard / Accident', description_en= 'A space accident is any accident involving space objects that cause damage (adapted from UNGA, 1971).', cluster_id = '1067' WHERE id = '78529';
	UPDATE public.hip_hazard SET code= 'ET0205', name_en= 'Near-Earth Object', description_en= 'A near-Earth object (NEO) is an asteroid or comet whose trajectory brings it to within 1.3 astronomical units of the Sun and hence within 0.3 astronomical units, or approximately 45 million kilometres, of the Earth’s orbit (UN OOSA, no date).', cluster_id = '1067' WHERE id = '78530';
	UPDATE public.hip_hazard SET code= 'GH0201', name_en= 'Earthquake', description_en= 'Earthquake is a term used to describe the sudden slip on a fault and the ground shaking that occurs from the radiated seismic energy during the slipping event. The sudden slip can be caused by stress changes in the earth or volcanic/magmatic activity (USGS, no date).', cluster_id = '1068' WHERE id = '78531';
	UPDATE public.hip_hazard SET code= 'GH0202', name_en= 'Lava Flows and Domes', description_en= 'Lava flows and lava domes form from molten rock that erupts and cools on or near the Earth’s surface. A lava flow is an outpouring of fluid, relatively low-viscosity molten rock, whereas a lava dome is a pile of relatively viscous lava that cannot flow far from the vent (cf. Dietterich et al., 2025; Harnett et al., 2025), the collapse of which may be hazardous.', cluster_id = '1069' WHERE id = '78539';
	UPDATE public.hip_hazard SET code= 'GH0203', name_en= 'Ash/Tephra Fall (including Volcanic Ballistic Projectiles)', description_en= 'Tephra is a collective term for volcanic fragments (pyroclasts) generated by the fragmentation of fresh magma and old (i.e., pre- existing) rocks ejected into the atmosphere during an explosive eruption, irrespective of size, composition and shape. The term ''volcanic ash'' refers to the finest particles of tephra (Tephra also include relatively large bombs (fragments of fresh magma) and blocks (fragments of pre-existing rocks) that are ejected during an explosive eruption at variable velocity and angle on cannon ball-like trajectories (Volcanic Ballistic Projectiles); they are not entrained within the volcanic plume, are not significantly affected by the wind field, and are dispersed in proximity to the vent (typically', cluster_id = '1069' WHERE id = '78540';
	UPDATE public.hip_hazard SET code= 'GH0204', name_en= 'Pyroclastic Density Current', description_en= 'Pyroclastic density currents are hot, fast-moving mixtures of volcanic particles and gas that flow according to their density relative to the surrounding medium and the Earth''s gravity. They typically originate from the gravitational collapse of explosive eruption columns, lava domes or lava-flow fronts, and explosive lateral blasts (cf. Charbonnier et al., 2025).', cluster_id = '1069' WHERE id = '78542';
	UPDATE public.hip_hazard SET code= 'GH0205', name_en= 'Lahars', description_en= 'Lahars are discrete, rapid, gravity-driven, water-saturated flows containing water and solid particles of volcanic rock, sediment, ice, wood, and other debris that originate at volcanoes (Gudmundsson, 2015; Vallance and Iverson, 2015).', cluster_id = '1069' WHERE id = '78543';
	UPDATE public.hip_hazard SET code= 'GH0206', name_en= 'Volcanic Gases and Aerosols', description_en= 'Volcanic gas includes any gas-phase substance that is emitted by volcanic or volcanic-geothermal activity. Volcanic aerosols include liquid or solid particles that are small enough to be suspended in the air, and that are emitted by volcanic or volcanic-geothermal activity (adapted from Baxter and Horwell, 2015, Fischer and Chiodini, 2015, and Williams-Jones and Rymer ,2015).', cluster_id = '1069' WHERE id = '78546';
	UPDATE public.hip_hazard SET code= 'GH0307', name_en= 'Liquefaction', description_en= 'Liquefaction refers to the loss of strength experienced in loosely packed, saturated or close to saturated sediments at or near the ground surface in response to strong ground shaking, such as earthquakes, cyclic loading (repeated application of stresses), and vibration from machinery, or due to the development of excess pore water pressure resulting from a change in head or confining pressures. (c. AGI, 2017; USGS, no date).', cluster_id = '1742' WHERE id = '78552';
	UPDATE public.hip_hazard SET code= 'GH0311', name_en= 'Surface Rupture and Fissuring', description_en= 'Surface ruptures and fissures are localized ground displacements that develop in response to tensional, compressional, and shear stresses, most commonly in unconsolidated sediment, but also in rock (Arizona Geological Survey, 2020). Surface ruptures represent the upward continuation of fault slip at depth, while fissures are smaller displacements, or more distributed deformation in and around the rupture area (adapted from USGS, no date and PNSN, no date).', cluster_id = '1070' WHERE id = '78553';
	UPDATE public.hip_hazard SET code= 'GH0309', name_en= 'Subsidence and Uplift', description_en= 'Subsidence is a lowering or collapse of the ground (BGS, 2020). Uplift is the converse.', cluster_id = '1742' WHERE id = '78554';
	UPDATE public.hip_hazard SET code= 'GH0310', name_en= 'Shrink-Swell Subsidence', description_en= 'Subsidence is a lowering or collapse of the ground, caused by various factors, including groundwater lowering, sub-surface mining or tunnelling, consolidation, sinkholes, or changes in moisture content in expansive soils. Shrink-swell is the term applied to the behaviour of expansive soils. These are a group of soils that exhibit volumetric change in response to changes in moisture content, such that they shrink in response to desiccation and swell by hydration, resulting in ground subsidence and ground heave respectively (BGS, 2020).', cluster_id = '1742' WHERE id = '78555';
	UPDATE public.hip_hazard SET code= 'GH0308', name_en= 'Sinkhole', description_en= 'Sinkholes (also known as dolines) are depressions or holes in the ground caused by the dissolution, collapse or erosion of rock below the landsurface. This is one of several hazards that result in subsidence, i.e., lowering or collapse of the ground (adapted from USGS, no date; and BGS, no date).', cluster_id = '1742' WHERE id = '78556';
	UPDATE public.hip_hazard SET code= 'GH0407', name_en= 'Ground Gases (CH4, Rn, etc.)', description_en= 'Ground gases are natural gases generated by various processes including material decay (natural and anthropogenic) and magma bodies (adapted from UK HMRC, 2016 and USGS, no date).', cluster_id = '1070' WHERE id = '78557';
	UPDATE public.hip_hazard SET code= 'GH0404', name_en= 'River Erosion and Accretion', description_en= 'River erosion is the removal of material from the banks and beds of rivers and streams (cf. Lawler, 1993). River accretion is the formation of new land such as channel bars, sandbanks and deltas by sedimentation or changing river flow (after Islam and Guchhait, 2020 and Hasanuzzaman et al., 2024).', cluster_id = '1070' WHERE id = '78558';
	UPDATE public.hip_hazard SET code= 'GH0406', name_en= 'Sand Encroachment', description_en= 'Sand encroachment is the accumulation of wind-borne sand. It commonly affects coasts, watercourses, and both cultivated and uncultivated land, typically occurring in arid to semi-arid regions. As sand accumulations move, they can bury towns, roads, oases, crops, market gardens, irrigation channels, and dams, leading to significant material and socioeconomic damage (cf. FAO, 2010).', cluster_id = '1070' WHERE id = '78559';
	UPDATE public.hip_hazard SET code= 'GH0306', name_en= 'Submarine Gravitational Mass Movement (‘Landslide’)', description_en= 'A submarine gravitational mass movement (‘Landslide’) is the downslope movement of sediment or rock due to gravity, occurring when the downslope forces exceed the sediment''s resistance to movement (Adapted from Lee et al., 2007).', cluster_id = '1742' WHERE id = '78561';
	UPDATE public.hip_hazard SET code= 'GH0301', name_en= 'Rock, debris and earth falls', description_en= 'A rock, debris or earth fall is a fragment of rock (a block), body of debris or earth (here taken to include mud) detached by sliding, toppling, or falling, that falls from a vertical or sub-vertical cliff and proceeds down slope by bouncing and flying along ballistic trajectories or by rolling on talus or debris slopes (adapted from Highland and Bobrowsky, 2008).', cluster_id = '1742' WHERE id = '78562';
	UPDATE public.hip_hazard SET code= 'GH0302', name_en= 'Rock, debris and earth spreads (including landscape creep)', description_en= 'Rock spread: Near-horizontal stretching (elongation) of a mass of coherent blocks of rock as a result of intensive deformation of an underlying weak material, or by multiple retrogressive sliding controlled by a weak basal surface. Usually with fairly limited total displacement and slow movement (Hungr et al., 2014).Debris spread requires formal definition, but is likely to include sub-categories based on type of material and the velocity of the mass movement (cf. Cruden and Varnes, 1996; Hungr et al., 2014). It includes Sand/silt liquefaction spread: extremely rapid lateral spreading of a series of soil blocks, floating on a layer of saturated (loose) granular soil, liquefied by earthquake shaking or spontaneous liquefaction (Hungr et al., 2014).Earth spread requires formal definition, but is likely to include sub-categories based on type of material and the velocity of the mass movement (cf. Cruden and Varnes, 1996; Hungr et al., 2014). It includes Sensitive clay spread: extremely rapid lateral spreading of a series of coherent clay blocks, floating on a layer of remoulded sensitive clay (Hungr et al., 2014).', cluster_id = '1742' WHERE id = '78563';
	UPDATE public.hip_hazard SET code= 'MH0101', name_en= 'Downburst', description_en= 'A downburst is a violent and damaging downdraught reaching the ground surface, associated with a severe thunderstorm (WMO, 1992).', cluster_id = '1071' WHERE id = '78566';
	UPDATE public.hip_hazard SET code= 'MH0102', name_en= 'Lightning (Electrical Storm)', description_en= 'Lightning is the luminous manifestation accompanying a sudden electrical discharge that takes place from or inside a cloud or, less often, from high structures on the ground or from mountains (WMO, 2017).', cluster_id = '1071' WHERE id = '78567';
	UPDATE public.hip_hazard SET code= 'MH0103', name_en= 'Thunderstorm', description_en= 'A thunderstorm is defined as one or more sudden electrical discharges, manifested by a flash of light (lightning) and a sharp or rumbling sound (thunder) (WMO, no date).', cluster_id = '1071' WHERE id = '78568';
	UPDATE public.hip_hazard SET code= 'MH0601', name_en= 'Coastal Flooding', description_en= 'Coastal flooding occurs from multiple sources, including storm surges, waves and swell, seiches, riverine and flash floods near the coast, tides, sea-level rise and tsunamis. It is most frequently the result of storm surges and high winds coinciding with high tides. The surge itself is the result of the raising of sea levels due to low atmospheric pressure. In particular configurations, such as major estuaries or confined sea areas, the piling up of water is amplified by a combination of the shallowing of the seabed and retarding of return flow (WMO, 2011; WMO, 2022).', cluster_id = '1072' WHERE id = '78569';
	UPDATE public.hip_hazard SET code= 'MH0602', name_en= 'Estuarine (Coastal) Flooding', description_en= 'Estuarine flooding is flooding over and near coastal areas caused by storm surges and high winds coincident with high tides, thereby obstructing the seaward river flow. Estuarine flooding can be caused by tsunamis in specific cases (WMO, 2011).', cluster_id = '1072' WHERE id = '78570';
	UPDATE public.hip_hazard SET code= 'MH0603', name_en= 'Flash Flooding', description_en= 'A flash flood is a flood of short duration with a relatively high peak discharge (WMO, 2021).', cluster_id = '1072' WHERE id = '78571';
	UPDATE public.hip_hazard SET code= 'MH0604', name_en= 'Fluvial (Riverine) Flooding', description_en= 'Overflowing by water of the normal confines of a watercourse or other body of water (WMO, 2012).', cluster_id = '1072' WHERE id = '78572';
	UPDATE public.hip_hazard SET code= 'MH0605', name_en= 'Groundwater Flooding', description_en= 'A groundwater flood is the emergence of groundwater at the ground surface away from perennial river channels or the rising of groundwater into man-made ground, under conditions where the ‘normal’ ranges of groundwater level and groundwater flow are exceeded (BGS, 2010).', cluster_id = '1072' WHERE id = '78573';
	UPDATE public.hip_hazard SET code= 'MH0608', name_en= 'Ice-Jam Flooding Including Debris', description_en= 'An ice-jam flood including debris is defined as an accumulation of shuga including ice cakes, below ice cover. It is broken ice in a river which causes a narrowing of the river channel, a rise in water level and local floods (WMO, 2012).Shuga is defined as the accumulation of spongy white ice lumps, a few centimetres across, formed from grease ice or slush, and sometimes from anchor ice rising to the surface (WMO, 2012).', cluster_id = '1072' WHERE id = '78574';
	UPDATE public.hip_hazard SET code= 'MH0609', name_en= 'Ponding (Drainage) Flooding', description_en= 'A ponding flood is a flood that results from rainwater ponding at or near the point where it falls because it is falling faster than the drainage system (natural or man-made) can carry it away (WMO, 2006).', cluster_id = '1072' WHERE id = '78575';
	UPDATE public.hip_hazard SET code= 'MH0610', name_en= 'Snowmelt Flooding', description_en= 'A snowmelt flood is a significant flood rise in a river caused by the melting of snowpack accumulated during the winter (WMO, 2012).', cluster_id = '1072' WHERE id = '78576';
	UPDATE public.hip_hazard SET code= 'MH0606', name_en= 'Surface Water Flooding', description_en= 'Surface water flooding is that part of the rain which remains on the ground surface during rain and either runs off or infiltrates after the rain ends, not including depression storage (WMO, 2012).', cluster_id = '1072' WHERE id = '78577';
	UPDATE public.hip_hazard SET code= 'MH0607', name_en= 'Glacial Lake Outburst Flooding', description_en= 'A ‘glacial lake outburst flood’ is a phrase used to describe a sudden release of a significant amount of water retained in a glacial lake, irrespective of the cause (Emmer, 2017).', cluster_id = '1072' WHERE id = '78578';
	UPDATE public.hip_hazard SET code= 'EN0104', name_en= 'Black Carbon', description_en= 'Black carbon refers to the substance formed through the incomplete combustion of fossil fuels, biofuels and biomass, which is emitted in both anthropogenic and naturally occurring soot. It consists of pure carbon in several linked forms. Black carbon warms the Earth by absorbing heat in the atmosphere and by reducing albedo – the ability to reflect sunlight – when deposited on snow and ice. It is operationally defined as an aerosol species based on measurement of light absorption and chemical reactivity and/or thermal stability (UNEP, 2019).', cluster_id = '1065' WHERE id = '78579';
	UPDATE public.hip_hazard SET code= 'MH0201', name_en= 'Dust storm or Sandstorm', description_en= 'A dust storm is an ensemble of particles of dust or sand energetically lifted to great heights by a strong and turbulent wind (WMO, 2017).', cluster_id = '1073' WHERE id = '78580';
	UPDATE public.hip_hazard SET code= 'MH0202', name_en= 'Fog', description_en= 'Fog is a suspension of very small, usually microscopic water droplets in the air, reducing visibility at the Earth’s surface (WMO, 2017).', cluster_id = '1073' WHERE id = '78581';
	UPDATE public.hip_hazard SET code= 'MH0203', name_en= 'Haze', description_en= 'Haze is a suspension in the air of extremely small, dry particles invisible to the naked eye and sufficiently numerous to give the air an opalescent appearance (WMO, 2017).', cluster_id = '1073' WHERE id = '78582';
	UPDATE public.hip_hazard SET code= 'MH0204', name_en= 'Sand haze', description_en= 'A dust haze (or sand haze) is a suspension in the air of dust or small sand particles, raised from the ground prior to the time of observation by a dust storm or sandstorm. The dust storm or sandstorm may have occurred either at or near the observation site or far from it (WMO, 2025).', cluster_id = '1073' WHERE id = '78584';
	UPDATE public.hip_hazard SET code= 'MH0205', name_en= 'Smoke', description_en= 'Smoke is a suspension in the air of small particles produced by combustion (WMO, 2017).', cluster_id = '1073' WHERE id = '78585';
	UPDATE public.hip_hazard SET code= 'EN0401', name_en= 'Ocean Acidification', description_en= 'Ocean acidification refers to a reduction in the pH of the ocean over an extended period, caused primarily by the uptake of carbon dioxide from the atmosphere. It can also result from other chemical additions to or subtractions from the ocean (IPCC, 2011).', cluster_id = '1065' WHERE id = '78586';
	UPDATE public.hip_hazard SET code= 'MH0701', name_en= 'Rogue Wave', description_en= 'Rogue waves are extreme waves with overall or crest heights that are abnormally high relative to the background significant wave height (WMO, 2018).', cluster_id = '1074' WHERE id = '78587';
	UPDATE public.hip_hazard SET code= 'EN0302', name_en= 'Saltwater Intrusion', description_en= 'Seawater intrusion is the process by which saltwater infiltrates a coastal aquifer, leading to contamination of fresh groundwater (Prince Edward Island Department of Environment, Labour and Justice, 2011).', cluster_id = '1065' WHERE id = '78588';
	UPDATE public.hip_hazard SET code= 'MH0706', name_en= 'Sea Ice (Icebergs)', description_en= 'Sea ice is any form of ice found at sea (WMO, 2015).', cluster_id = '1074' WHERE id = '78589';
	UPDATE public.hip_hazard SET code= 'MH0707', name_en= 'Ice Flow', description_en= 'Ice flow is the motion of ice driven by gravitational forces, ice stress or, for sea ice, wind, water currents and tide (AMS, 2012).', cluster_id = '1074' WHERE id = '78590';
	UPDATE public.hip_hazard SET code= 'MH0702', name_en= 'Seiche', description_en= 'Seiches are sea-level oscillations at the resonant frequency of enclosed bodies of water (WMO, 2011).', cluster_id = '1074' WHERE id = '78591';
	UPDATE public.hip_hazard SET code= 'MH0703', name_en= 'Storm Surge', description_en= 'A storm surge reflects the difference between the actual water level under the influence of a meteorological disturbance (storm tide) and the level which would have occurred in the absence of the meteorological disturbance (i.e., astronomical tide) (WMO, 2008, 2011, 2017).', cluster_id = '1074' WHERE id = '78592';
	UPDATE public.hip_hazard SET code= 'MH0704', name_en= 'Storm Tides', description_en= 'A storm tide is the actual sea level as influenced by a weather disturbance. The storm tide consists of the normal astronomical tide plus the storm surge (WMO, 2017).', cluster_id = '1074' WHERE id = '78593';
	UPDATE public.hip_hazard SET code= 'MH0705', name_en= 'Tsunami', description_en= 'Tsunami, a Japanese term meaning ''wave'' (‘nami’) in a harbour (‘tsu’), refers to a series of long-period travelling waves, typically caused by disturbances such as earthquakes occurring beneath or near the ocean floor (IOC, 2019).', cluster_id = '1074' WHERE id = '78594';
	UPDATE public.hip_hazard SET code= 'MH0306', name_en= 'Depression or Cyclone (Low Pressure Area)', description_en= 'A depression or cyclone is a region of the atmosphere in which the pressure is lower than that of the surrounding region at the same level (WMO, 1992).', cluster_id = '1079' WHERE id = '78595';
	UPDATE public.hip_hazard SET code= 'MH0307', name_en= 'Extra-tropical Cyclone', description_en= 'An extra-tropical cyclone is a low-pressure system, which develops in latitudes outside the tropics (WMO, 1992).', cluster_id = '1079' WHERE id = '78596';
	UPDATE public.hip_hazard SET code= 'MH0308', name_en= 'Sub-Tropical Cyclone', description_en= 'A subtropical cyclone is a non-frontal, low-pressure system that has characteristics of both tropical and extra-tropical cyclones. Like tropical cyclones, they are non-frontal, synoptic-scale cyclones that originate over tropical or subtropical waters and have a closed surface wind circulation about a well-defined centre (WMO, 2024 a).', cluster_id = '1079' WHERE id = '78597';
	UPDATE public.hip_hazard SET code= 'EN0105', name_en= 'Acid Rain', description_en= 'Acid rain is rain that, in the course of its history, has combined with chemical elements or pollutants in the atmosphere and reaches the Earth’s surface as a weak acid solution (WMO/UNESCO, 2012).', cluster_id = '1065' WHERE id = '78598';
	UPDATE public.hip_hazard SET code= 'MH0403', name_en= 'Blizzard', description_en= 'A blizzard is a severe snowstorm characterised by poor visibility, usually occurring at high latitudes and in mountainous regions (WMO, 1992).', cluster_id = '1076' WHERE id = '78599';
	UPDATE public.hip_hazard SET code= 'MH0401', name_en= 'Drought', description_en= 'A drought is a period of abnormally dry weather characterised by a prolonged deficiency of precipitation below a certain threshold over a large area and a period longer than a month (WMO, 2023).', cluster_id = '1076' WHERE id = '78600';
	UPDATE public.hip_hazard SET code= 'MH0404', name_en= 'Hail', description_en= 'Hail is precipitation in the form of particles of ice (hailstones). These can be either transparent or partly or completely opaque. They are usually spheroidal, conical or irregular in form, and generally 5−50 mm in diameter. The particles may fall from a cloud either separately or agglomerated in irregular lumps (WMO, 2017).', cluster_id = '1076' WHERE id = '78601';
	UPDATE public.hip_hazard SET code= 'MH0405', name_en= 'Snow', description_en= 'Snow is the precipitation of ice crystals, isolated or agglomerated, falling from a cloud (WMO, 2017).', cluster_id = '1076' WHERE id = '78603';
	UPDATE public.hip_hazard SET code= 'MH0406', name_en= 'Snow Storm', description_en= 'A snow storm is a meteorological disturbance giving rise to a heavy fall of snow, often accompanied by strong winds (WMO, 1992).', cluster_id = '1076' WHERE id = '78604';
	UPDATE public.hip_hazard SET code= 'MH0502', name_en= 'Cold Wave', description_en= 'A cold wave is a period of marked and unusual cold weather characterised by a sharp and significant drop in air temperatures near the surface (maximum, minimum and daily average) over a large area and persisting below certain thresholds for at least two consecutive days during the cold season (WMO, 2020).', cluster_id = '1077' WHERE id = '78605';
	UPDATE public.hip_hazard SET code= 'MH0503', name_en= 'Dzud', description_en= 'A dzud (a Mongolian term that describes ‘severe winter conditions’’, sometimes spelled zud) is a cold-season disaster in which anomalous climatic (i.e., heavy snow and severe cold) and/or land-surface (snow/ ice cover and lack of pasture) conditions lead to reduced accessibility and/or availability of forage/pastures, and ultimately to high livestock mortality during winter–spring (Natsagdorj & Dulamsuren, 2001).', cluster_id = '1077' WHERE id = '78606';
	UPDATE public.hip_hazard SET code= 'MH0504', name_en= 'Freeze', description_en= 'A freeze is an air temperature equal to or less than the freezing point of water (0°C) (adapted from WMO, 1992).', cluster_id = '1077' WHERE id = '78607';
	UPDATE public.hip_hazard SET code= 'MH0505', name_en= 'Frost (Hoar Frost)', description_en= 'A hoar frost is a deposit of ice produced by the deposition of water vapour from the surrounding air and is generally crystalline in appearance (WMO, 2017).', cluster_id = '1077' WHERE id = '78608';
	UPDATE public.hip_hazard SET code= 'MH0506', name_en= 'Freezing Rain (Ice storm)', description_en= 'Freezing rain is rain where the temperature of the water droplets is below 0°C. Drops of supercooled rain may freeze on impact with the ground, in-flight aircraft or other objects (WMO, 2017).', cluster_id = '1077' WHERE id = '78609';
	UPDATE public.hip_hazard SET code= 'MH0507', name_en= 'Glaze', description_en= 'Glaze is a smooth compact deposit of ice, generally transparent, formed by the freezing of super-cooled drizzle droplets or raindrops on objects with a surface temperature below or slightly above 0°C (WMO, 2017).', cluster_id = '1077' WHERE id = '78610';
	UPDATE public.hip_hazard SET code= 'MH0508', name_en= 'Ground Frost', description_en= 'Ground frost is a covering of ice, in one of its many forms, produced by the sublimation of the water vapour on objects colder than 0°C (WMO, 1992).Ground frost occurs when the temperature of the upper layer of the soil is less than 0°C (WMO, 1992).', cluster_id = '1077' WHERE id = '78611';
	UPDATE public.hip_hazard SET code= 'MH0501', name_en= 'Heatwave', description_en= 'A heatwave is a marked, unusual period of hot weather over a region persisting for at least two or three consecutive days and nights during the hot period of the year based on local climatological conditions, with thermal conditions recorded above given thresholds (WMO and WHO, 2015).', cluster_id = '1077' WHERE id = '78612';
	UPDATE public.hip_hazard SET code= 'MH0509', name_en= 'Icing (Including Ice)', description_en= 'Icing refers to any deposit or coating of ice on an object caused by the impact of liquid hydrometeors, usually supercooled (WMO, 1992).', cluster_id = '1077' WHERE id = '78613';
	UPDATE public.hip_hazard SET code= 'MH0510', name_en= 'Thaw', description_en= 'Thaw is the melting of snow or ice at the Earth’s surface due to a temperature rise above 0°C (WMO, 1992).', cluster_id = '1077' WHERE id = '78614';
	UPDATE public.hip_hazard SET code= 'MH0801', name_en= 'Avalanche', description_en= 'An avalanche is a mass of snow and ice falling suddenly down a mountain slope and often taking with it earth, rocks and rubble of every description (WMO, 1992).', cluster_id = '1078' WHERE id = '78615';
	UPDATE public.hip_hazard SET code= 'MH0302', name_en= 'Derecho', description_en= 'Derechos are fast-moving bands of thunderstorms with destructive winds. The winds can be as strong as those found in hurricanes or even tornadoes. Unlike hurricanes and tornadoes, these winds follow straight lines (NOAA, 2019).', cluster_id = '1079' WHERE id = '78618';
	UPDATE public.hip_hazard SET code= 'MH0303', name_en= 'Gale (Strong Gale)', description_en= 'A gale is wind with a speed of between 34 and 40 knots (62–74 km/h, 32–38 mph). Also known as Beaufort scale wind force 8 (WMO, 1992).', cluster_id = '1079' WHERE id = '78619';
	UPDATE public.hip_hazard SET code= 'MH0304', name_en= 'Squall', description_en= 'A squall is an atmospheric phenomenon characterised by a very large variation of wind speed: it begins suddenly, has a duration of the order of minutes and decreases suddenly in speed. It is often accompanied by a shower or thunderstorm (WMO, 2018).', cluster_id = '1079' WHERE id = '78620';
	UPDATE public.hip_hazard SET code= 'MH0309', name_en= 'Tropical Cyclone', description_en= 'A tropical cyclone is a cyclone of tropical origin of small diameter (some hundreds of kilometres) with a minimum surface pressure in some cases of less than 900 hPa, very violent winds and torrential rain; sometimes accompanied by thunderstorms. It usually contains a central region, known as the ''eye'' of the storm, with a diameter of the order of some tens of kilometres, and with light winds and a more or less lightly clouded sky (WMO, 2023).Alternative definition: A tropical cyclone is a warm-core, non-frontal synoptic-scale cyclone, originating over tropical or subtropical waters, with organised deep convection and closed surface wind circulation about a well-defined centre (WMO, 2024).Note: Typhoon, hurricane, cyclone, and tropical cyclone are different terms for the same weather phenomenon in different geographical regions (WMO, 2023):In the western North Atlantic, central and eastern North Pacific, Caribbean Sea and Gulf of Mexico, such a weather phenomenon is called a ‘hurricane’;In the western North Pacific, it is called a ‘typhoon;In the Bay of Bengal and Arabian Sea, it is called a ‘cyclone’; In the western South Pacific and southeast Indian Ocean, it is called a ‘severe tropical cyclone’;In the southwest India Ocean, it is called a ‘tropical cyclone’.', cluster_id = '1079' WHERE id = '78622';
	UPDATE public.hip_hazard SET code= 'MH0305', name_en= 'Tornado', description_en= 'A tornado is a rotating column of air extending from the base of a cumuliform cloud and often visible as a condensation funnel in contact with the ground, and/or attendant circulating dust or debris cloud at the ground (WMO, 2017).', cluster_id = '1079' WHERE id = '78624';
	UPDATE public.hip_hazard SET code= 'MH0301', name_en= 'Wind', description_en= 'Wind is air motion relative to the Earth’s surface. Unless otherwise specified, only the horizontal component is considered (WMO, 1992).', cluster_id = '1079' WHERE id = '78625';
	UPDATE public.hip_hazard SET code= 'SO0101', name_en= 'International Armed Conflict (IAC)', description_en= 'International armed conflict covers all cases of declared war and other de facto armed conflict between two or more States, even if the state of war is not recognised by one of them and/or the use of armed force is unilateral (ICRC, 2024).', cluster_id = '1080' WHERE id = '78626';
	UPDATE public.hip_hazard SET code= 'SO0102', name_en= 'Non-International Armed Conflict (NIAC)', description_en= 'It is widely accepted today that two key conditions must be met for a situation of violence to be considered a Non-International Armed Conflict (NIAC) and therefore subject to International humanitarian law (IHL): the non-state party/parties must be organized, and the violence between the parties must be sufficiently intense. The existence of a NIAC is not predicated on any other threshold or condition. In particular, political or other motivations of the parties play no role in the classification of a conflict. (adapted from ICRC, 2024)', cluster_id = '1080' WHERE id = '78627';
	UPDATE public.hip_hazard SET code= 'SO0103', name_en= 'Civil Unrest', description_en= 'Civil unrest is an umbrella term for a wide spectrum of social and/or political phenomena, and although there is no commonly agreed definition, the term is used widely among United Nations agencies, funds and programmes.A suggested definition for civil unrest is as follows: sporadic but continued collective physical violence in a context of social or political instability, that may result in deaths, injury and destruction. At times, non-violent collective action (such as protests, demonstrations, etc.) - exercising the right to peaceful assembly - or mass gatherings, when intersecting with external actors or factors, may lead to violence (Adapted from Kalyvas, 2000).', cluster_id = '1080' WHERE id = '78628';
	UPDATE public.hip_hazard SET code= 'SO0201', name_en= 'Explosive Ordnance', description_en= 'Explosive ordnance, including explosive remnants of war, is interpreted as encompassing the following munitions: mines, cluster munitions, unexploded ordnance, abandoned ordnance, booby traps and other devices (IMAS 4.10) that remain in the environment following the cessation of armed conflict.Explosive remnants of war refer to unexploded ordnance and abandoned explosive ordnance that are left by a party to an armed conflict following the cessation of warfare. Unexploded ordnance are munitions that have been primed, fused, armed or otherwise prepared for use, and may have been fired, dropped, launched or projected yet remain unexploded through malfunction or design or for any other reason. Abandoned ordnance refers to explosive ordnance that has not been used during an armed conflict but has been left behind or dumped. Mines are munitions designed to be placed under, on or near the ground or other surface area and to be exploded by the presence, proximity or contact of a person or a vehicle.', cluster_id = '1081' WHERE id = '78629';
	UPDATE public.hip_hazard SET code= 'SO0202', name_en= 'Environmental Degradation from Conflict', description_en= 'Environmental degradation is both a driver and consequence of disasters and conflict, reducing the capacity of the environment to meet social and ecological needs (Adapted from UNDRR, 2022).', cluster_id = '1081' WHERE id = '78630';
	UPDATE public.hip_hazard SET code= 'SO0301', name_en= 'Violence', description_en= 'Violence is a social phenomenon that involves forceful acts or behaviour that are intended to cause harm. The injury or damage inflicted by violence to an individual or collective group may be physical, psychological, sexual, or deprivation, or combined. Violence is both intentional and forceful (Adapted from Jacquette, 2013).', cluster_id = '1082' WHERE id = '78631';
	UPDATE public.hip_hazard SET code= 'SO0302', name_en= 'Stampede or Crushing (Human)', description_en= 'Stampede or crushing is the surge of individuals in a crowd, in response to real or perceived danger or loss of physical space. It often disrupts the orderly movement of crowds resulting in movement for self-protection leading to increased localised crowd density and physical compression of the human bodies (Adapted from Burkle & Hsu, 2011; Illiyas et al., 2013; Ngai, et al., 2009).', cluster_id = '1082' WHERE id = '78632';
	UPDATE public.hip_hazard SET code= 'SO0401', name_en= 'Financial shock', description_en= 'A financial shock is an unexpected disturbance which originates from the financial sector and has a significant effect on an economy (e.g. national, regional, or global). The term is largely used to refer to events which have negative impacts (ECB, 2013).', cluster_id = '1083' WHERE id = '78633';
	UPDATE public.hip_hazard SET code= 'TL0601', name_en= 'Radioactive Waste', description_en= 'Radioactive waste is radioactive material for which no further use is foreseen but still contains, or is contaminated with, radionuclides. Radioactive waste can be in gas, liquid or solid form (IAEA, 2018). It may remain radioactive from a few hours to hundreds of thousands of years.For legal and regulatory purposes, material for which no further use is foreseen that contains, or is contaminated with, radionuclides at activity concentrations greater than clearance levels as established by the regulatory body (Adapted from IAEA, 2018 and IAEA 2022 a).', cluster_id = '1084' WHERE id = '78634';
	UPDATE public.hip_hazard SET code= 'TL0602', name_en= 'Radioactive Agents &amp; Material', description_en= 'A substance or a material emitting, or related to the emission of, ionizing radiation (either in the form of electro-magnetic waves or particle radiation) is radioactive. Depending on the magnitude of exposure, the radioactive substance may affect human health; as such it is subject to regulatory control by national laws and national regulatory authorities. Radioactive material may also be a hazard to animal health, other forms of life and the environment (IAEA, 2018).', cluster_id = '1084' WHERE id = '78635';
	UPDATE public.hip_hazard SET code= 'TL0603', name_en= 'Nuclear Agents', description_en= 'Nuclear agents are derived from neutron radiation (n) which is a neutron emitted by an unstable nucleus, in particular during atomic fission and nuclear fusion. Apart from a component in cosmic rays, neutrons are usually produced artificially. Because they are electrically neutral particles, neutrons can be very penetrating and when they interact with matter or tissue, they cause the emission of beta- and gamma-radiation. Neutron radiation therefore requires heavy shielding to reduce exposure (IAEA, 2004).', cluster_id = '1084' WHERE id = '78637';
	UPDATE public.hip_hazard SET code= 'TL0306', name_en= 'Explosive agents', description_en= 'An explosive substance or agent is a solid or liquid substance (or mixture of substances) which is in itself capable, by chemical reaction, of producing gas at such a temperature and pressure and at such a speed as to cause damage to the surroundings. Pyrotechnic substances and mixtures are included even when they do not evolve gases. Explosions can cause multiple severely injured casualties in a single incident (Adapted from UN 2023 and UK Parliament POSTNOTE 2011).', cluster_id = '1089' WHERE id = '78638';
	UPDATE public.hip_hazard SET code= 'TL0201', name_en= 'Building Collapse', description_en= 'Building collapse is the failure of load-bearing structural elements, causing a building to fall or fail catastrophically / catastrophic failure (adapted from US Department of Labor, no date).', cluster_id = '1086' WHERE id = '78639';
	UPDATE public.hip_hazard SET code= 'TL0202', name_en= 'Building, highrise, cladding', description_en= 'A building high-rise cladding fire hazard occurs when combustible materials such as cladding on a high-rise building greatly increases risk in the event of a fire and can have a catastrophic outcome (adapted from Rockpanel, no date)', cluster_id = '1086' WHERE id = '78640';
	UPDATE public.hip_hazard SET code= 'TL0203', name_en= 'Structural Failure', description_en= 'Structural failure corresponds to the exceedance of ultimate limit state in many of the load-carrying elements, which compromise the structural stability of the building (Rossetto, 2013).', cluster_id = '1086' WHERE id = '78641';
	UPDATE public.hip_hazard SET code= 'TL0204', name_en= 'Bridge Failure', description_en= 'Bridge failure is the inability of a bridge, or its components, to perform as specified by its design and construction requirements (Wardhana and Hadipriono, 2003).Note: This definition includes bridges that have totally collapsed, partially collapsed and those that experienced distress, such as, exhibiting excessive deformation.', cluster_id = '1086' WHERE id = '78642';
	UPDATE public.hip_hazard SET code= 'TL0205', name_en= 'Dam Failure', description_en= 'Dam failure is the uncontrolled release of water due to structural collapse, foundation instability, or overtopping, posing risks on people and property downstream (ICOLD, 2023, mentioned in Moreno-Rodenas et al., 2025).', cluster_id = '1086' WHERE id = '78643';
	UPDATE public.hip_hazard SET code= 'TL0206', name_en= 'Supply Chain Failure', description_en= 'Supply chain failure refers to an event in the supply chain that disrupts the flow of materials on their journey from initial suppliers through to final customers (Walters, 2007).', cluster_id = '1086' WHERE id = '78644';
	UPDATE public.hip_hazard SET code= 'TL0207', name_en= 'Critical Infrastructure Failure', description_en= 'Critical Infrastructure failure is defined as the failure in one or more of the physical structures, facilities, networks and other assets which provide services that are essential to the social and economic functioning of a community or society (UNGA, 2016).Critical Infrastructures as described in the ANNEX of (CER - DIRECTIVE (EU) 2022/2557 of the European Parliament and of the council) can be defined as: Energy (e.g., Electricity, District heating and cooling, Oli, Gas, Hydrogen); Transport (Air, Rail, Water, Road, Public Transport); Banking; Financial market infrastructure; Health; Drinking water; Waste water; Digital Infrastructure; Public administration; Space (European Parliament 2022).', cluster_id = '1086' WHERE id = '78645';
	UPDATE public.hip_hazard SET code= 'TL0208', name_en= 'Nuclear Plant Failure', description_en= 'Nuclear plant failure occurs when the accidental melting of the core of a nuclear reactor results in a complete or partial core collapse (adapted from USNRC, 1975).', cluster_id = '1086' WHERE id = '78646';
	UPDATE public.hip_hazard SET code= 'TL0209', name_en= 'Power Outage/ or Blackout', description_en= 'In the electric power domain, especially in power transmission and distribution, a power outage usually refers to a partial or total loss of power supply to some end user (e.g., population, enterprises, critical systems). Triggering factors may include accidents, equipment breakdowns, failure of control mechanisms, targeted attacks (physical or cyber), organisational errors, and natural hazards (adapted from Pescaroli et al., 2017; UK Cabinet Office, 2017; EIS Council, 2019; FEMA, 2018).', cluster_id = '1086' WHERE id = '78647';
	UPDATE public.hip_hazard SET code= 'TL0211', name_en= 'Emergency Telecommunications Failure', description_en= 'Emergency telecommunications failure is an umbrella term for telecommunications of an ‘extraordinary nature’ under abnormal and potentially adverse network conditions (ITU, 2007).', cluster_id = '1086' WHERE id = '78648';
	UPDATE public.hip_hazard SET code= 'TL0210', name_en= 'Water Supply Failure', description_en= 'Water supply failure is the physical shortage or scarcity in access of water supply due to the failure of institutions to ensure a regular supply or due to a lack of adequate infrastructure (adapted from UN-Water, no date).Alternative definition: Water supply systems are networks whose edges and nodes are pressure pipes and either pipe junctions, water sources or end-users. Water supply systems are designed to protect the customer from natural biological contamination, and the same systems have potential efficacy against deliberate biological and chemical contamination (adapted from Franchin and Cavalieri, 2013; and Jain et al., 2014).', cluster_id = '1086' WHERE id = '78649';
	UPDATE public.hip_hazard SET code= 'TL0212', name_en= 'Radio and Other Telecommunication Failures', description_en= 'Radio and other telecommunication failures occur when there is an internal or external interruption of communications by either party that results in difficulty transporting a message as it was intended (adapted from Dainty et al., 2007).', cluster_id = '1086' WHERE id = '78650';
	UPDATE public.hip_hazard SET code= 'TL0101', name_en= 'Malware', description_en= 'Malware is a summary term for different forms of malevolent software designed to infiltrate and infect computers, typically without the knowledge of the owner (ITU, 2008).', cluster_id = '1088' WHERE id = '78653';
	UPDATE public.hip_hazard SET code= 'TL0102', name_en= 'Data Breach &amp; PII Breach', description_en= 'A data breach occurs when the data for which a company/organisation is responsible suffers a security incident resulting in a breach of confidentiality, availability or integrity (European Commission, no date).', cluster_id = '1088' WHERE id = '78654';
	UPDATE public.hip_hazard SET code= 'TL0106', name_en= 'Cyberbullying', description_en= 'Cyberbullying is bullying that takes place using digital devices such as cell/mobile phones, computers, and tablets. Cyberbullying can occur through SMS, e-mail, apps, social media, forums, or gaming when people view, participate in, or share content. Cyberbullying includes the deliberate sending, posting, or sharing of negative, harmful, false, or mean content about someone else. It can include sharing personal or private information about someone else causing embarrassment or humiliation. Some cyberbullying may also be unlawful or criminal behaviour (adapted from UNICEF, no date; PHE, 2014; US Government, no date).', cluster_id = '1088' WHERE id = '78659';
	UPDATE public.hip_hazard SET code= 'TL0309', name_en= 'Natech', description_en= 'Natural hazard triggered technological accident (Showalter et al., 1994).', cluster_id = '1089' WHERE id = '78660';
	UPDATE public.hip_hazard SET code= 'TL0302', name_en= 'Pollution', description_en= 'Pollution is the presence of substances and heat in environmental media (air, water, land) whose nature, location, or quantity produces undesirable environmental effects; and the activities that generates pollutants. (UN data, no date).', cluster_id = '1089' WHERE id = '78661';
	UPDATE public.hip_hazard SET code= 'TL0304', name_en= 'Explosion', description_en= 'Explosion-related technological incidents can be defined as accidental or intentional rapid energetic events that result in the actual or potential exposure of responders and/or members of the public to a chemical hazard (adapted from WHO, no date).', cluster_id = '1089' WHERE id = '78662';
	UPDATE public.hip_hazard SET code= 'TL0301', name_en= 'Leaks and Spills', description_en= 'A leak or a spill is an incident involving the uncontrolled release of a toxic substance, potentially resulting in harm to public health and the environment. Chemical incidents can occur as a result of natural events, or as a result of accidental or intentional events. These incidents can be sudden and acute or have a slow onset when there is a ‘silent’ release of a chemical. Leaks and spills can range from small releases to full-scale major emergencies (adapted from WHO, no date).', cluster_id = '1089' WHERE id = '78663';
	UPDATE public.hip_hazard SET code= 'TL0303', name_en= 'Soil Pollution', description_en= 'Soil pollution refers to the presence of a chemical or substance out of place and/or present in a soil at higher-than-normal concentration that has adverse effects on any non-targeted organism (Rodríguez-Eugenio et al., 2018).', cluster_id = '1089' WHERE id = '78664';
	UPDATE public.hip_hazard SET code= 'TL0305', name_en= 'Fire', description_en= 'Fire-related technological incidents can be defined as accidental or intentional events that result in the actual or potential physical damage to property and exposure of responders and/or members of the public to a chemical hazard (adapted from WHO, no date)', cluster_id = '1089' WHERE id = '78665';
	UPDATE public.hip_hazard SET code= 'TL0307', name_en= 'Mining Hazards', description_en= 'Mining hazards may cause major environmental and health impacts such as pollution of water bodies, degradation of forest resources, depletion of soil nutrients, destruction of wildlife habitat, and threats to human health. (adapted from UNDP and UN Environment, 2018).', cluster_id = '1089' WHERE id = '78666';
	UPDATE public.hip_hazard SET code= 'TL0308', name_en= 'Safety Hazards Associated with Oil and Gas', description_en= 'Oil and gas extraction, and associated servicing activities involve many types of equipment and materials. Identifying and controlling hazards is critical to preventing injuries and deaths (US Department of Labor, no date).Alternative definition: For the purpose of the C155 - Occupational Safety and Health Convention, 1981 (No. 155) (ILO, 1981):the term ''branches of economic activity'' covers all branches in which workers are employed, including the public service.the term ''workers'' covers all employed persons, including public employees.the term ''workplace'' covers all places where workers need to be or to go by reason of their work and which are under the direct or indirect control of the employer.the term ''regulations'' covers all provisions given force of law by the competent authority or authorities.the term ''health'', in relation to work, indicates not merely the absence of disease or infirmity; it also includes the physical and mental elements affecting health which are directly related to safety and hygiene at work.', cluster_id = '1089' WHERE id = '78667';
	UPDATE public.hip_hazard SET code= 'TL0501', name_en= 'Disaster and Conflict Waste', description_en= 'Disaster and conflict waste is the waste generated by the impact of a disaster or conflict, both as a direct effect of the disaster or conflict as well as in the post-disaster and post-conflict phase as a result of poor waste management (UNEP/OCHA, 2011).', cluster_id = '1090' WHERE id = '78668';
	UPDATE public.hip_hazard SET code= 'TL0502', name_en= 'Solid Waste', description_en= 'Solid waste covers discarded materials that are no longer required by the owner or user. Solid waste includes materials that are in a solid or liquid state but excludes wastewater and small particulate matter released into the atmosphere (United Nations, 2014).', cluster_id = '1090' WHERE id = '78669';
	UPDATE public.hip_hazard SET code= 'TL0503', name_en= 'Wastewater', description_en= 'Wastewater is regarded as a combination of one or more of the following materials: domestic effluent consisting of ‘blackwater’ (excreta, urine and faecal sludge, contaminants from pharmaceutical and personal care products) and ‘greywater’ (used water from washing and bathing); water from commercial establishments and institutions, including hospitals; industrial effluent, stormwater and other urban runoff; and agricultural, horticultural and aquaculture runoff (UNEP, 2023a).', cluster_id = '1090' WHERE id = '78670';
	UPDATE public.hip_hazard SET code= 'TL0504', name_en= 'Hazardous Waste', description_en= 'Hazardous waste is waste that has physical, chemical, or biological characteristics such that it requires special handling and disposal procedures to avoid negative health effects, adverse environmental effects or both (Joint UNEP/OCHA Environment Unit, 2011).', cluster_id = '1090' WHERE id = '78671';
	UPDATE public.hip_hazard SET code= 'TL0505', name_en= 'Plastic Waste', description_en= 'Plastic Waste is defined as any discarded plastic (organic, or synthetic, material derived from polymers, resins or cellulose) generated by any industrial process, or by consumers. (Source: GEMET/APD).', cluster_id = '1090' WHERE id = '78672';
	UPDATE public.hip_hazard SET code= 'TL0507', name_en= 'Electronic Waste (E-Waste)', description_en= 'Electrical and electronic waste, or E-waste, refers to electrical or electronic equipment that is waste, including all components, sub-assemblies and consumables that are part of the equipment at the time the equipment becomes waste (UNEP, 2019a).', cluster_id = '1090' WHERE id = '78673';
	UPDATE public.hip_hazard SET code= 'TL0508', name_en= 'Health-care Waste', description_en= 'Health-care waste is a by-product of health care that includes sharps, non-sharp blood contaminated items, blood, body parts and tissues, chemicals, pharmaceuticals and radioactive materials. Safe management of health-care waste protects health-care workers, waste handlers, patients and their families and the community to preventable infections, toxic effects and injuries (adapted from WHO, no date, and WHO, 2017).', cluster_id = '1090' WHERE id = '78674';
	UPDATE public.hip_hazard SET code= 'TL0509', name_en= 'Landfilling', description_en= 'Landfill is the final placement of waste in or on the land in a controlled or uncontrolled way according to different sanitary, environmental protection and other safety requirements (UN Data, no date).', cluster_id = '1090' WHERE id = '78675';
	UPDATE public.hip_hazard SET code= 'TL0511', name_en= 'Tailings', description_en= 'Tailings are a by-product of mining, consisting of the processed rock or soil left over from the separation of the commodities of value from the rock or soil within which they occur (adapted from GISTM, 2020).', cluster_id = '1090' WHERE id = '78676';
	UPDATE public.hip_hazard SET code= 'TL0510', name_en= 'Waste Treatment Lagoons', description_en= 'Waste [treatment] lagoons can be defined as impoundments made by excavation or earth fill for biological treatment of animal and other agricultural waste (Spellman & Bieber, 2012).', cluster_id = '1090' WHERE id = '78677';
	UPDATE public.hip_hazard SET code= 'TL0506', name_en= 'Marine Debris', description_en= 'Marine debris is any persistent, manufactured or processed solid material discarded, disposed of or abandoned in the marine and coastal environment. Marine litter consists of items that have been made or used by people and deliberately discarded into the sea or rivers or on beaches; brought indirectly to the sea with rivers, sewage, stormwater or winds; or accidentally lost, including material lost at sea in bad weather (adapted from UN Environment, no date and NOAA, no date).', cluster_id = '1090' WHERE id = '78678';
	UPDATE public.hip_hazard SET code= 'TL0214', name_en= 'Drain and Sewer Flooding', description_en= 'Drain and sewer flooding is said to occur when sewage or foul water leaks from the sewerage system (through pipes, drains or manholes) or floods up through toilets, sinks or showers inside a building (Priestly, 2016).', cluster_id = '1086' WHERE id = '78679';
	UPDATE public.hip_hazard SET code= 'TL0215', name_en= 'Reservoir Flooding', description_en= 'Man-made reservoirs, sometimes called artificial lakes, are important water sources in many countries around the world. In contrast to natural processes of lake formation, reservoirs are artificial, usually formed by constructing a dam across a river or by diverting a part of the river flow and storing the water in a reservoir. Reservoir flooding occurs as an uncontrolled release of water if a dam or reservoir fails (adapted from UNEP, 2000 and Defra and EA, 2014).', cluster_id = '1086' WHERE id = '78680';
	UPDATE public.hip_hazard SET code= 'TL0401', name_en= 'Air Transportation Accident', description_en= 'An air transportation accident is defined as an occurrence associated with the operation of an aircraft which takes place between the time any person boards the aircraft with the intention of flight until such time as all such persons have disembarked, in which one of the following applies: a person is fatally or seriously injured, the aircraft sustains damage or structural failure, and the aircraft is missing or is completely inaccessible (United Nations, European Union, the International Transport Forum at the OECD, 2019:119).', cluster_id = '1093' WHERE id = '78681';
	UPDATE public.hip_hazard SET code= 'TL0402', name_en= 'Inland Water Ways Transportation Accident', description_en= 'An inland waterway transportation accident is an unwanted or unintended sudden event or a specific chain of such events occurring in connection with inland water vessel operations, which have harmful consequences (United Nations, European Union and the International Transport Forum at the OECD, 2019).', cluster_id = '1093' WHERE id = '78682';
	UPDATE public.hip_hazard SET code= 'TL0403', name_en= 'Maritime Accident', description_en= 'A maritime accident is an event, or a sequence of events, that has resulted in any of the following occurring directly in connection with the normal operation of a marine vessel: the death of, or serious injury to, a person; the loss of a person from a ship; the loss, presumed loss or abandonment of a marine vessel; material damage to a marine vessel; the stranding or disabling of a marine vessel, or the involvement of a marine vessel in a collision; material damage to the marine infrastructures external to a vessel, that could seriously endanger the safety of the vessel or another vessel or an individual; and severe damage to the environment, or the potential for severe damage to the environment, brought about by the damage of a marine vessel (United Nations, European Union and the International Transport Forum at the OECD, 2019).', cluster_id = '1093' WHERE id = '78683';
	UPDATE public.hip_hazard SET code= 'TL0404', name_en= 'Rail Accident', description_en= 'A rail accident is a sudden event or a specific chain of such events (occurring during train operation) which has harmful consequences (United Nations, European Union and the International Transport Forum at the OECD, 2019).', cluster_id = '1093' WHERE id = '78684';
	UPDATE public.hip_hazard SET code= 'TL0405', name_en= 'Road Traffic Accident', description_en= 'A road traffic accident involving at least one road vehicle in motion on a public road or private road to which the public has right of access, resulting in at least one injured or killed person. Approximately 1.19 million people die each year as a result of road traffic crashes, which are the leading cause of death for children and young adults aged 5-29 years with 92% of the world''s fatalities on the roads occurring in low- and middle-income countries, even though these countries have around 60% of the world''s vehicles (adapted from UNECE, Eurostat, ITF, 2019 and WHO, 2023a).', cluster_id = '1093' WHERE id = '78685';

		

	-- 6. Migrage specific hazards in hazarous_event, disaster_event and disaster_records,
	-- Set hip_hazard_id to null for all removed specific hazards.
    UPDATE public.hazardous_event 
    SET hip_hazard_id = NULL 
    WHERE hip_hazard_id = ANY(removed_specific_hazards_ids);

    UPDATE public.disaster_event 
    SET hip_hazard_id = NULL 
    WHERE hip_hazard_id = ANY(removed_specific_hazards_ids);

	UPDATE public.disaster_records 
    SET hip_hazard_id = NULL 
    WHERE hip_hazard_id = ANY(removed_specific_hazards_ids);


	-- 6. Migrage cluster types in hazarous_event, disaster_event and disaster_records,
	-- Set hip_cluster_id to null for all removed specific hazards.
	UPDATE public.hazardous_event 
    SET hip_cluster_id = NULL 
    WHERE hip_cluster_id = ANY(removed_cluster_type_ids);

    UPDATE public.disaster_event 
    SET hip_cluster_id = NULL 
    WHERE hip_cluster_id = ANY(removed_cluster_type_ids);

	UPDATE public.disaster_records 
    SET hip_cluster_id = NULL 
    WHERE hip_cluster_id = ANY(removed_cluster_type_ids);

	-- 7. Delete removed specific hazards
	DELETE FROM public.hip_hazard 
	WHERE id = ANY(removed_specific_hazards_ids);
   
    -- 8. Delete removed cluster types
    DELETE FROM public.hip_cluster
    WHERE id = ANY(removed_cluster_type_ids);

   
END $$;